const judgePoint24 = `
const judgePoint24 = function (nums) {
  const isValid = x => Math.abs(x - 24) < 0.0000001
  const aux = (arr = []) => {
    if (arr.length === 1) {
      return isValid(arr[0])
    }
    let valid = false
    for (let i = 0; i < arr.length; i++) {
      for (let j = i + 1; j < arr.length; j++) {
        const nextArr = arr.filter((x, index) => index !== i && index !== j)
        valid = valid || aux([...nextArr, arr[i] + arr[j]])
          || aux([...nextArr, arr[i] - arr[j]])
          || aux([...nextArr, arr[j] - arr[i]])
          || aux([...nextArr, arr[i] * arr[j]])
          || aux([...nextArr, arr[i] / arr[j]])
          || aux([...nextArr, arr[j] / arr[i]])
      }
    }
    return valid
  }

  return aux(nums)
}

console.log(judgePoint24([4, 1, 8, 7]))`.trim();

const find132pattern = `
const find132pattern = function (nums) {
  let max = -Infinity
  const stack = []
  for (let i = nums.length - 1; i >= 0; i--) {
    while (nums[i] > stack[stack.length - 1]) {
      max = stack.pop()
    }
    if (nums[i] < max) {
      return true
    }
    stack.push(nums[i])
  }
  return false
}

