import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';


const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})


const SearchSchema = `
const SearchSchema = new Schema({
  name: { type: String, required: true },
  date: { type: Date, default: Date.now }
});

module.exports = Item = mongoose.model('item', SearchSchema);`.trim();

const routesSearch = `
const Item = require('../models/search');

router.get('/search', (req, res) => {
  Item.find()
    .sort({ date: -1 })
    .then(items => res.json(items));
});

router.get("/search/:name", (req, res) => {
  var regex = new RegExp(req.params.name, 'i');
  Item.find({ name: regex }).then((result) => {
    res.status(200).json(result)
  })
})`.trim();

// const arraysSchema = ``.trim();


class SearchErrorHandling extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0)
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4><Sidebar /></h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>Search error Handling</h3>
              <b>model/search.js</b>
              <div style={titles}>
                <PrismCode
                  code={SearchSchema}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <b>routes/search.js</b>
              <div style={titles}>
                <PrismCode
                  code={routesSearch}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>

              <div style={titles}>
                <PrismCode
                  code={nested}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <b>PluralizedCollectioName.js</b>
              <div style={titles}>
                <PrismCode
                  code={PluralizedCollectioName}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <b>primaryUniqeId.js</b>
              <div style={titles}>
                <PrismCode
                  code={primaryUniqeId}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <b>renameDoc.js</b>
              <div style={titles}>
                <PrismCode
                  code={renameDoc}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <b>timeStamps.js</b>
              <div style={titles}>
                <PrismCode
                  code={timeStamps}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
             <br/>
             <br/>

             <h3></h3>
            </List>
          </Paper>
        </Grid>
      </Grid>
    )
  }
}

export default (withStyles(styles)(SearchErrorHandling));
