    return axios.get("https://jsonplaceholder.typicode.com/posts");
  };

  useEffect(() => {
    Promise.all([getMockdata, getMockdata2()]).then((res) => {
      setMockdata(res[0].data);
      setMockdata2(res[1].data);
    });
  }, []);

  return (
    <div>
      <h1>Data One</h1>
      <ul>
        {mockdata.map((item) => (
          <li key={item.id}>{item.name}</li>
        ))}
      </ul>
      <h1>Data Two</h1>
      <ul>
        {mockdata2.map((item) => (
          <li key={item.id}>{item.title}</li>
        ))}
      </ul>
    </div>
  );
}; 
`.trim();

const progressbar = `
const App = () => {
  const [progress, setProgress] = useState(0);

  const handleClick = () => {
    if (progress < 100) {
      setProgress(progress + 10);
    }
  };

  const progressStyle = {
    width: '$'{progress}%,
    height: "20px",
    backgroundColor: "green",
  };

  return (
    <div>
      <div style={progressStyle}>{progress}%</div>
      <button onClick={handleClick}>Progress</button>
    </div>
  );
};
`.trim();

const mouseHovers = `
const App = () => {
  const [isHovering, setHovering] = useState(false);

  const handleMouseHover = () => {
    setHovering(isHovering => !isHovering);
  }

  return (
    <div>
      <div
        onMouseEnter={handleMouseHover}
        onMouseLeave={handleMouseHover}
      >
        Hover Me
      </div>
      {isHovering && <div>Hovering right me</div>}
    </div>
  );
}`.trim();

const getLists = `
const App = () => {
  const [mockdata, setMockdata] = useState([]);

  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/albums/1/photos")
      .then((res) => res.json())
      .then((res) => {
        setMockdata(res);
      });
  }, []);

  const handleSearch = (e) => {
    let value = e.target.value.toLowerCase();
    let result = [];

    result = mockdata.filter((data) => {
      return data.title.search(value) != -1;
    });
    setMockdata(result);
  };

  return (
    <div>
      <input type="text" onChange={(event) => handleSearch(event)} />
      {mockdata.map((items) => (
        <li>{items.title}</li>
      ))}
    </div>
  );
};
`.trim();

const MoveButton = `
const MoveButton = (props) => {
  return (
      <button onClick={props.onClick}>
          Click To Move
      </button>
  );
}

const BoxOne = () => <p>Box1</p>;

const BoxTwo = () => <p>Box2</p>;


class App extends Component {
state = { positions: 0 }

handleClick = () => {
  this.setState({ positions: (this.state.positions + 1) % 3 })
}

render () { 
const positions = this.state.positions;
  return (
      <div>
          { positions === 0 ? <MoveButton onClick={this.handleClick}/> : ''}
          <BoxOne />
          { positions === 1 ? <MoveButton onClick={this.handleClick}/> : ''}
          <BoxTwo />
          { positions === 2 ? <MoveButton onClick={this.handleClick}/> : ''}
      </div>
  );
}
}

export default App;`.trim();

