class App extends Component {
   state = { inputValue: "" };

 handleUpdate = (e) => {
   if (e.target.validity.valid) {
     this.setState({ inputValue: e.target.value }); 
   }
 }

 reset = () => {
   this.setState({ inputValue: "" }); 
 }

 render() {
   return (
     <div>
       <input type="number" value={this.state.inputValue} onChange={this.handleUpdate} step="any" />
       <button onClick={this.reset}>reset</button>
     </div>
   )
 }  
 }`.trim();

const dateTime = `
 //1
 function date_time() {
   return Date();
 }

 //2
 function formatDate(dayOfWeek, day, month, year) {
   var daysOfWeek = ["Sun","Mon","Tue","Wed","Thu","Fri","Sat"];
   var months = ["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];
       return daysOfWeek[dayOfWeek] + " " + months[month] + " " + day + " " + year; }

   var birthday = new Date(Date.UTC(2000,0,1)); 
   var birthDay = formatDate(birthday.getUTCDay(), birthday.getUTCDate(),     
   birthday.getUTCMonth(), birthday.getUTCFullYear())


 export { date_time, birthDay }`.trim();

class AsyncAwait extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0);
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4>
              <Sidebar />
            </h4>
          </Paper>
        </Grid>

        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>1. Get Lists</h3>
              <div style={titles}>
                <PrismCode
                  code={getLists}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>1. Filter/Dropdawn</h3>
              <div style={titles}>
                <PrismCode
                  code={getLists}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>
                3. Recived data from two different APIS in one function to
                multiple calls.
              </h3>
              <div style={titles}>
                <PrismCode
                  code={multiple}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>5. Progress incrase upto 100%</h3>
              <div style={titles}>
                <PrismCode
                  code={progressbar}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>5. Mouse Hover on Card</h3>
              <div style={titles}>
                <PrismCode
                  code={mouseHovers}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>6.onClick move button</h3>
              <div style={titles}>
                <PrismCode
                  code={MoveButton}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>7. Step Input</h3>
              <div style={titles}>
                <PrismCode
                  code={steps}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>8. Date_time</h3>
              <div style={titles}>
                <PrismCode
                  code={dateTime}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
            </List>
          </Paper>
        </Grid>
      </Grid>
    );
  }
}

export default withStyles(styles)(AsyncAwait);
