import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';

import Stcksval from '../../../assets/stcks.png';

const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const redesign = {
  height: 200,
  width: 500
}

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})


const twoSum = `
function twoSum(arr, s){
  var sum=[];
  for(let i=0; i<arr.length; i++){
    for(let j=i+1; j<arr.length; j++){
      if(arr[i]+arr[j] == s){
        sum.push([arr[i], arr[j]])
      }
    }
  }
  return sum;
}

console.log(twoSum([1,2,3,4],5));`.trim()

const twoSums = `
const twoSum = (nums, target) => {
  const memo = {}
  const aux = (index) => {
    if (memo[nums[index]] !== undefined) {
      return [memo[nums[index]], index]
    }
    memo[target - nums[index]] = index
    return aux(index + 1)
  }
  return aux(0, nums.length - 1)
}

console.log(twoSum([2,7,11,15],18))`.trim();

const shuffle = `
function shuffle(nums, n) {
  const result = []
  for (let i = 0; i < n; i++) {
    result.push(nums[i], nums[i + n])
  }
  return result
};

console.log(shuffle([2,5,1,3,4,7], 3))`.trim();

const threeSumClosest = `
const threeSumClosest = function (nums, target) {
  let diff = Infinity
  let result = 0
  nums.sort((a, b) => a - b)
  for (let i = 0; i < nums.length; i++) {
    let left = i + 1
    let right = nums.length - 1
    while (left < right) {
      const sum = nums[i] + nums[left] + nums[right]
      const currentDiff = sum - target
      if (currentDiff > 0) {
        right -= 1
      } else {
        left += 1
      }
      if (Math.abs(currentDiff) < diff) {
        result = sum
      }
      diff = Math.min(Math.abs(currentDiff), diff)
    }
  }
  return result
}

console.log(threeSumClosest([-1,2,1,-4]))`.trim();


class DSLogic7 extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0)
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4><Sidebar /></h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
            <h3>1.Two Sum</h3>
              Check whether any two numbers in an array sums to a given number
              <div style={titles}>
                <PrismCode
                  code={twoSum}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>2. Two Sum.</h3>
              Description<br/>
              Given an array of integers nums and an integer target, return indices of the two numbers such that they add up to target.
              <br/>
              You may assume that each input would have exactly one solution, and you may not use the same element twice.
              <br/>
              You can return the answer in any order.
              <br/>
              <br/>


              <b>Ex. </b>
              <br/>
              Input: nums = [2,7,11,15], target = 9<br/>
              Output: [0,1]<br/>
              Output: Because nums[0] + nums[1] == 9, we return [0, 1].
              <div style={titles}>
                <PrismCode
                  code={twoSums}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>





              <h3>4. Shuffle the Array.</h3>
              <b>Input: </b>nums = [2,5,1,3,4,7], n = 3<br/>
              <b>Output: </b>[2,3,5,4,1,7] <br/>
              <b>Explanation: </b>Since x1=2, x2=5, x3=1, y1=3, y2=4, y3=7 then the answer is [2,3,5,4,1,7].
              <div style={titles}>
                <PrismCode
                  code={shuffle}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>2. 3Sum Closest</h3>
              Given an array nums of n integers and an integer target, find three integers in nums such that the sum is closest to target. Return the sum of the three integers. You may assume that each input would have exactly one solution.
              <br/>
Example 1:<br/>

Input: nums = [-1,2,1,-4], target = 1<br/>
Output: 2<br/>
Explanation: The sum that is closest to the target is 2. (-1 + 2 + 1 = 2).
              <div style={titles}>
                <PrismCode
                  code={threeSumClosest}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>







            </List>
          </Paper>
        </Grid>
      </Grid>
    )
  }
}

export default (withStyles(styles)(DSLogic7));
