import { Observable } from 'rxjs';

@Component({
  selector: 'app-root',
  template: '<p>{{ data }}</p>',
})
export class AppComponent {
  data: any = '';

  ngOnInit() {
    const myObservable = new Observable((observer) => {
      setTimeout(() => {
        observer.next('Hello');
      }, 1000);

      setTimeout(() => {
        observer.next('World!');
        observer.complete();
      }, 2000);
    });
    myObservable
      .toPromise()
      .then((datas) => {
        this.data = datas;
      })
      .catch((error) => {
        console.error(error);
      });
  }
}
`.trim();
