import React, { Component } from "react";
import Prism from "prismjs";
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import "../../ReactJs/styles.css";
import Sidebar from "../sidebar";
import PrismCode from "../../ReactJs/prismCode";

const titles = { backgroundColor: "#F0F8FF", padding: "1px", fontSize: "16px" };

const styles = (theme) => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  actionDiv: {
    textAlign: "center",
  },
});

const subscribes = `
obs.subscribe({
  next:(data)=>console.log('obj',data),
  error:(err)=>console.log('obj',err),
  complete:()=>console.log('complate'),
})`.trim();

const rxjsfrom = `
//import { from } from 'rxjs';
export class AppComponent  {
  ngOnInit(){
    const data = from(fetch('https://jsonplaceholder.typicode.com/posts'));

    data.subscribe({
      next(res) { console.log(res); },
      error(err) { console.error('Error: ' + err); },
      complete() { console.log('Completed'); }
    });
  }
}
`.trim();

const mapfil = `
//import { of } from 'rxjs';
//import { map } from 'rxjs/operators';

export class AppComponent  {
  val:any=[];

  ngOnInit(){
    const nums = of(1, 2, 3);

    const squr = map((val: number) => val * val);
    const sqNum = squr(nums);

    sqNum.subscribe(x => this.val.push(x));
  }
}
`.trim();

const mapipe = `
//import { of } from 'rxjs';
//import { filter, map } from 'rxjs/operators';

export class AppComponent  {
  val:any=[];

  ngOnInit(){
    const squareOdd = of(1, 2, 3, 4, 5)
    .pipe(
      filter(n => n % 2 !== 0),
      map(n => n * n)
    );

    squareOdd.subscribe(x => this.val.push(x));
  }
}
`.trim();

const stcreate = `
//import { Component, ElementRef, ViewChild } from '@angular/core';
//import { fromEvent } from 'rxjs';

export class AppComponent  {
  constructor(){}
  @ViewChild('addBtn')
  addBtn!: ElementRef;

  //stream create
  ngAfterViewInit(){
    fromEvent(this.addBtn.nativeElement, 'click').subscribe(res=>{
      console.log(res)
    })
  }
}
`.trim();

const unsubscribe = `
//import { interval, Subscription } from 'rxjs';
export class AppComponent  {
  intervalval=0;
  videoSub!: Subscription;

  ngOnInit(){
    const broadcast = interval(1000)
    this.videoSub = broadcast.subscribe(res=>{
      this.intervalval = res;

      if(res>5){
        this.videoSub.unsubscribe()
      }
    })
  }
}
`.trim();

const filter = `
import {interval} from 'rxjs';
import {filter, map} from "rxjs/operators";

const observable = interval(100).pipe(filter(data=>{
  return data<5
}));

observable.subscribe(data => {
  console.log(data);
})
`.trim();

const observableFun = `import { from } from 'rxjs'; // from function
const data = from(fetch('/api/endpoint')); //Created from Promise
data.subscribe({
 next(response) {console.log(response);},
 error(err) {console.error('Error: ' + err);},
 complete() {console.log('Completed');}
});`.trim();

const placeholders = `
//app.component.ts
import {PlaceholderService} from "./services/placeholder.service"

@Component({
  selector: 'app-root',
  template: '
  <button (click)="getApi()">GET</button>
    <div *ngFor="let items of data">
      <p>{{items.id}} - {{items.title}}</p>
    </div>
  ',
})

export class AppComponent implements OnInit{
  data:Array<any>;

  constructor( private https:PlaceholderService) { 
    this.data=new Array<any>()
   }

  ngOnInit(): void {}
  getApi(){
    this.https.getData().subscribe((data)=>{
      this.data=data
    })
  }
}


//services/placeholder.service.ts
import { Injectable } from '@angular/core';
import {HttpClient} from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class PlaceholderService {

  constructor(private http:HttpClient) { }

  getData(): Observable<any>{
    const url="https://jsonplaceholder.typicode.com/posts";
    return this.http.get<any>(url)
  }
}
`.trim();

const otsbservable = `
import { Observable } from "rxjs/Observable";

const obs = new Observable((observer)=>{
  let counter=1;
  const producer = setInterval(()=>{
    observer.next(counter++)
  },1000)
})

const sub = obs.subscribe({
  next:(data)=>console.log('obj1',data),
  error:(err)=>console.log('obj1',err),
  complete:()=>console.log('complate'),
})

setTimeout(()=>{
  sub.unsubscribe();
},5000)`.trim();

const otsbservable2 = `
import { Observable } from "rxjs/Observable";

const obs = new Observable((observer) => {
  let counter = 1;
  const producer = setInterval(() => {
    observer.next(counter++);

    if (counter > 5) {
      observer.error("oops");
      observer.complete();
    }
  }, 1000);

  return () => {
    console.log("unsubscribe done");
    clearInterval(producer);
  };
});

const sub = obs.subscribe({
  next: (data) => console.log("obj1", data),
  error: (err) => console.log("obj1", err),
  complete: () => console.log("complate"),
});`.trim();

const otsbservable3 = `
import { Observable } from "rxjs/Observable";

const obs = new Observable((observer)=>{
  let counter=1;
  const producer = setInterval(()=>{
    observer.next(counter++)

    if(counter>5){
      observer.error('oops')
    }
  },1000);

  return()=>{
    console.log('unsubscribe done')
    clearInterval(producer)
  }
})

const sub = obs.subscribe({
  next:(data)=>console.log('obj1',data),
  error:(err)=>console.log('obj1',err),
  complete:()=>console.log('complate'),
})

setTimeout(()=>{
  const sub2 = obs.subscribe({
    next:(data)=>console.log('obj2',data),
    error:(err)=>console.log('obj2',err),
    complete:()=>console.log('complate2'),
  })
},3000)

setTimeout(()=>{
  sub.unsubscribe();
},5000);`.trim();

const otsbservable4 = `
import { Observable } from 'rxjs';

@Component({
  selector: 'app-root',
  template: '<p>{{ data }}</p>',
})
export class AppComponent {
  data: any = '';

  ngOnInit() {
    const myObservable = new Observable((observer) => {
      setTimeout(() => {
        observer.next('Hello');
      }, 1000);

      setTimeout(() => {
        observer.next('World!');
        observer.complete();
      }, 2000);
    });
    myObservable
      .toPromise()
      .then((datas) => {
        this.data = datas;
      })
      .catch((error) => {
        console.error(error);
      });
  }
}
`.trim();

const otsbservable5 = `
import { of } from 'rxjs';

@Component({
  selector: 'app-root',
  template: '{{ values | async }}',
})
export class AppComponent {
  values = of(1, 2, 3, 4, 5);
}
`.trim();

const otsbservable6 = `
import { from } from 'rxjs';

@Component({
  selector: 'app-root',
  template: '<div>{{ data$ | async }}</div>',
})
export class AppComponent {
  data$ = from([1, 2, 3, 4, 5]);

  constructor() {
    this.data$.subscribe((value) => console.log(value));
  }
}
`.trim();

const otsbservable7 = `
import { fromEvent } from 'rxjs';

@Component({
  selector: 'app-root',
  template: '<button #myButton>Click me!</button>',
})
export class AppComponent {
  constructor(private elRef: ElementRef) {}

  ngOnInit() {
    const button = this.elRef.nativeElement.querySelector('button');
    const click$ = fromEvent(button, 'click');

    click$.subscribe((event) => {
      console.log('Button clicked!');
    });
  }
}
`.trim();

const otsbservable8 = `
import { interval } from 'rxjs';

@Component({
  selector: 'app-root',
  template: '<p>{{ counter }}</p>',
})
export class AppComponent {
  counter: number = 0;

  ngOnInit() {
    interval(1000).subscribe((value) => {
      this.counter = value;
    });
  }
}
`.trim();

const otsbservable9 = `
import { timer } from 'rxjs';

@Component({
  selector: 'app-root',
  template: '<p>{{ count }}</p>',
})
export class AppComponent {
  count: number = 0;

  constructor() {
    const source = timer(0, 1000); // emits a value after a delay of 0ms, then every 1000ms

    source.subscribe((val) => {
      this.count = val;
    });
  }
}
`.trim();

const otsbservable10 = `
import { range } from 'rxjs';

@Component({
  selector: 'app-root',
  template: '{{ numbers$ | async }}',
})
export class AppComponent {
  numbers$ = range(1, 5);
}
`.trim();

const otsbservable11 = `
import { generate } from "rxjs";

generate({
  initialState: 1,
  condition: (a) => a < 10,
  iterate: (a) => a + 1,
  resultSelector: (b: number) => "Count" + b,
}).subscribe(console.log);
`.trim();

const otsbservable12 = `
import { ajax } from "rxjs/ajax";

const http$ = ajax("https://jsonplaceholder.typicode.com/posts");
http$.subscribe({
  next: (res) => {
    console.log(res.response);
  },
  error: (err) => {
    console.log("Error", err);
  },
});

//2
const http2$ = ajax({
  url: "https://jsonplaceholder.typicode.com/posts",
  headers: {
    Authorization: "Bearer",
    Accept: "application/json",
  },
});
http2$.subscribe({
  next: (res) => {
    console.log(res.response);
  },
  error: (err) => {
    console.log("Error", err);
  },
});

//3
const http3$ = ajax({
  url: "https://jsonplaceholder.typicode.com/posts",
  method: "POST",
  headers: {
    Authorization: "Bearer",
    Accept: "application/json",
  },
  body: {
    title: "foo",
    body: "bar",
    userId: 1,
  },
});
http3$.subscribe({
  next: (res) => {
    console.log(res.response);
  },
  error: (err) => {
    console.log("Error", err);
  },
});

//4
const http4$ = ajax.get("https://jsonplaceholder.typicode.com/posts", {
  "x-some-header-key": "value",
});
http4$.subscribe({
  next: (res) => {
    console.log(res.response);
  },
  error: (err) => {
    console.log("Error", err);
  },
});

//I5 f response come from server is html type
const http5$ = ajax({
  url: "https://jsonplaceholder.typicode.com/posts",
  responseType: "text",
});
