import React, { Component } from "react";
import Prism from "prismjs";
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import "../../ReactJs/styles.css";
import Sidebar from "../sidebar";
import PrismCode from "../../ReactJs/prismCode";

const titles = { backgroundColor: "#F0F8FF", padding: "1px", fontSize: "16px" };

const styles = (theme) => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  actionDiv: {
    textAlign: "center",
  },
});



const modifier = `
//Public
class Student {  
    public studCode: number;  
    studName: string;  
}  

let stud = new Student();  
stud.studCode = 101;  
stud.studName = "Joe Root";  

console.log(stud.studCode+ " "+stud.studName);  


//Private
class Student {  
public studCode: number;  
private studName: string;  

constructor(code: number, name: string){  
this.studCode = code;  
this.studName = name;  
}  

public display() {  
return ('My unique code: '$'{this.studCode}, my name: '$'{this.studName}.');  
    }  
}  

let student: Student = new Student(1, "JoeRoot");  
console.log(student.display());  


//Protected
class Student {  
    public studCode: number;  
    protected studName: string;  
    constructor(code: number, name: string){  
        this.studCode = code;  
        this.studName = name;  
        }  
}  
class Person extends Student {  
    private department: string;  

    constructor(code: number, name: string, department: string) {  
        super(code, name);  
        this.department = department;  
    }  
    public getElevatorPitch() {  
        return ('My unique code: '$'{this.studCode}, my name: '$'{this.studName} 
        and I am in '$'{this.department} Branch.');
    }
}
let joeRoot: Person = new Person(1, "JoeRoot", "CS");
console.log(joeRoot.getElevatorPitch());`.trim();

const accessor = `
