import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';


const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})


const todoList = ``.trim();

const viewEjs = `

<p>
  <%= post.body.substring(0, 100) + "..." %>
  <a href="<%= "/posts/" + post.title %>">Read More</a>
</p>
<% });%>
<%- include("partials/footer") -%>


//views/post.ejs
<%- include("partials/header") -%>

<h1><%=postTitle%></h1>
<p><%=postBody%></p>

<%- include("partials/footer") -%>
`.trim();

const partials = `
//views/partials/header.ejs
<head>
  <meta charset="utf-8">
  <title>Daily Journal</title>
  <meta charset="utf-8">
  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/css/bootstrap.min.css">
  <link rel="stylesheet" href="/css/styles.css">
  <nav class="navbar navbar-default">
    <div class="container">
      <div class="navbar-header">
        <p class="navbar-brand">DAILY JOURNAL</p>
      </div>
        <ul class="nav navbar-nav navbar-right">
          <!-- 6 -->
          <li id="home"><a href="/">HOME</a></li>
          <li id="about"><a href="/about">ABOUT</a></li>
          <li id="contact"><a href="/contact">CONTACT</a></li>
        </ul>
    </div>
  </nav>

  <body>
    <div class="container">


//views/partials/footer.ejs
</div>
<div class="footer-padding"></div>
  <div class="footer">
    <p>Anand Batjargal 2019</p>
  </div>
</div>
</body>
`.trim();


const fruitsProject = `
const mongoose = require('mongoose');

mongoose.connect("mongodb://localhost:27017/fruitsDB", { useNewUrlParser: true });

const fruitSchema = new mongoose.Schema({
  name: {
    type: String,
    required: [true, "What's the name of the fruit?"]
  },
  rating: {
    type: Number,
    min: 1,
    max: 10
  },
  review: String
});

// First Parm: String, singular, name of collection,
// in MongoDB it will be saved all lower case
// Second Parm: Scheme
const Fruit = mongoose.model("Fruit", fruitSchema);

const fruit = new Fruit({
  name: "Apple",
  rating: 7,
  review: "Pretty solid as a fruit."
});

//fruit.save();

const personSchema = new mongoose.Schema({
  name: String,
  age: Number,
  favoriteFruit: fruitSchema
});

const Person = mongoose.model("Person", personSchema);

//personJohn.save();

const banana = new Fruit({
  name: "Banana",
  rating: 6,
  review: "Weird texture."
});

const person = new Person({
  name: "Kevin",
  age: 25,
  favoriteFruit: banana
});

const orange = new Fruit({
  name: "Orange",
  rating: 8,
  review: "Tastes great."
});

// person.save();
Person.updateOne({ name: "John" }, { favoriteFruit: orange }, function (err) {
  if (err) {
    console.log(err);
  } else {
    console.log("Success");
  }
});

const kiwi = new Fruit({
  name: "Kiwi",
  rating: 4,
  review: "Too sweet for me."
});

Fruit.insertMany([banana, kiwi, orange], function (err) {
  if (err) {
    console.log(err);
  } else {
    console.log("Successfully saved all the fruits");
  }
});

Fruit.find(function (err, fruits) {
  if (err) {
    console.log(err);
  } else {
    mongoose.connection.close();

    fruits.forEach(function (fruit) {
      console.log(fruit.name);
    });
  }
});
`.trim();


class Ejs extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0)
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4><Sidebar /></h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>1. ToDo List</h3>
              <div style={titles}>
                <PrismCode
                  code={todoList}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />
              <b>ejs</b>
              <div style={titles}>
                <PrismCode
                  code={todoListEjs}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>2. Blog-website</h3>
              <div style={titles}>
                <PrismCode
                  code={blogWebsite}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <b>views/</b>
              <div style={titles}>
                <PrismCode
                  code={viewEjs}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <b>views/partials</b>
              <div style={titles}>
                <PrismCode
                  code={partials}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>3. Fruits</h3>
              <div style={titles}>
                <PrismCode
                  code={fruitsProject}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
            </List>
          </Paper>
        </Grid>
      </Grid>
    )
  }
}

export default (withStyles(styles)(Ejs));
