import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';


const titles = {backgroundColor:'#F0F8FF', padding:'1px', fontSize:'16px'}

const styles = theme => ({
  paper: {
      margin: theme.spacing(1),
      padding: theme.spacing(1)
  },
  smMargin: {
      margin: theme.spacing(1)
  },
  actionDiv: {
      textAlign: "center"
  }
})

const Create_DB = `
use EmployeeDB
db.dropDatabase()
db.createUser({user:"brad", pwd:"123", roles:["readWrite", "dbAdmin"]});

db.createCollection('store');                                          //create collection
db.orders.renameCollection('store')                                    //rename collection
 `.trim()

const findOne = `
db.store.findById(_id);
db.store.find()
db.store.findOne({country: "UK"});
db.store.find({name: 'Ritesh'}, {country: 'India', color: 'Green'})
`.trim()

const queries = `> show dbs
> use store
> db.createCollection('store');
> show collections
> db.store.insert({'interger':23});
> db.store.find().pretty();
> db.store.drop()
> db.store.insert({_id:1,name:'mukesh',country:'indian'});
> db.store.insertOne({_id:1,name:'mukesh',country:'indian'});

db.store.insertMany(
   [
     { _id: 20, devname: "John Wick", tools: "Visual Studio", born: 1948 },
     { _id: 21, devname: "Ganesh Roy", tools: "Net Beans", born: 1945 },
     { _id: 22, devname: "Deeksha Raul", tools: "Unity 3D", born: 1954 }
   ]);`.trim()

const Filtering = `> db.store.find({name:'mukesh'});
> db.store.find({$and: [{name:'mukesh'},{country:'indian'}] });
> db.user.find({},{"first_name":1,_id:0}).limit(2);
> db.user.find().count();
> db.user.find().limit(4);
> db.customers.find({},{"first_name":1,_id:0}).limit(1).skip(1);
> db.store.find({name:{$in: ["mukesh"]}});
> db.store.find({"store.name": {$not: {$eq: 24}}}).pretty()
> db.store.find({"store.name": {$not: {$in: ["mukesh"]}}}).pretty()
> db.store.find().pretty().limit(2)
> db.store.find().pretty().skip(1)

db.store.update({_id: 1}, { $set: { name: ["Vocals", "Violin", "Octapad"] }})

 > db.store.update({_id:2},{$set:{name:['mukesh'],salery:['1234567890']}});

 db.store.remove({ name: "mukesh" })
db.store.remove({})
`.trim()

const joining = `> db.users.find()
{ "_id" : 1, "userId" : 1, "name" : "Al" }
{ "_id" : 2), "userId" : 2, "name" : "Betty" }
{ "_id" : 3, "userId" : 3, "name" : "Cameron" }

> db.comments.find()
{ "_id" : 1, "userId" : 1, "comment" : "Hi, I'm Al and I love comments." }
{ "_id" : 2, "userId" : 1, "comment" : "Hi, it's Al again. I really do love comments." }
{ "_id" : 3, "userId" : 2, "comment" : "I'm Betty. This is my first comment onthis site." }
{ "_id" : 4, "userId" : 3, "comment" : "This is Cameron. I enjoyed reading your website." }
`.trim()

const aggregate = `db.users.aggregate([ {$lookup: {
                            from: "comments",
                            localField: "userId",
                            foreignField: "userId",
                            as: "combined"
                          }
                       }
                     ]).pretty()


var MongoClient = require('mongodb').MongoClient;
var url = "mongodb://127.0.0.1:27017/";
MongoClient.connect(url, (err, db) => {
            if (err) throw err;
            var dbo = db.db("mydb");

  dbo.collection('orders').aggregate([ { $lookup: {
                from: 'products',
                localField: 'product_id',
                foreignField: '_id',
                as: 'orderdetails'
              }
            }
          ]).toArray(function(err, res) {
              if (err) throw err;
                console.log(JSON.stringify(res));
                db.close();
            });`.trim()

const  Data_Modelling = `db.customers.insert([
                      {
                        _id: ,
                        Emp_ID: "10025AE336"
                        Personal_details:{
                        First_Name: "Radhika",
                        Last_Name: "Sharma",
                        Date_Of_Birth: "1995-09-26"
                      },

                        Contact: {
                          e-mail: "radhika_sharma.123@gmail.com",
