 }
 `.trim()

const Example = `{
  _id: POST_ID
  title: TITLE_OF_POST,
  description: POST_DESCRIPTION,
  by: POST_BY,
  url: URL_OF_POST,
  tags: [TAG1, TAG2, TAG3],
  likes: TOTALL_LIKES,

    comments: [{
      user:'COMMENT_BY',
      message: TEXT,
      dateCreated: DATE_TIME,
      like: LIKES
      },

      {
      user:'COMMENT_BY',
      message: TEXT,
      dateCreated: DATE_TIME,
      like: LIKES
      }
    ]
 }
  `.trim()

const Aggregation = `db.mycol.aggregate([{$group : {_id : "$by_user", num_tutorial : {$sum : 1}}}]);
`.trim()

const Pipeline = `> db.user.find()
{ "_id" : 1, "name" : "GENWI", "founded_year" : 2010 }
{ "_id" : 2, "name" : "Needium", "founded_year" : 2010 }
{ "_id" : 3, "name" : "Ziippi", "founded_year" : 2011 }
{ "_id" : 4, "name" : "Pixelmatic", "founded_year" : 2011 }
{ "_id" : 5, "name" : "Clowdy", "founded_year" : 2013 }


Pipeline
db.user.aggregate([
  { $match : { founded_year : 2011 } },
  { $sort : {name:-1} },
  { $project : { _id : 0, name : 1 } }
 ])


 Or Pipeline
 db.user.aggregate([
  { $match: { founded_year: { $gte: 2010 } } },
  { $group: {
  _id: "$founded_year",
  companies: { $push: "$name" }
  }},
  { $sort: { "_id": 1 } }
 ])`.trim()

const project = `db.user.aggregate([ { $project : { _id : 0, name : 1 } }])
`.trim()

const match = `db.user.aggregate({ $match: { founded_year: { $gte: 2010 } }})`.trim()

const group = `db.user.aggregate({ $group :{_id:ObjectId("5ef64c903da2b374c85626a1"), count:{
  $sum:1}} })`.trim()

const sort = `db.user.aggregate({ $sort : {name:-1} })
`.trim()

const unwind = `db.user.aggregate({ $project : {
                          author : 1 ,
                          title : 1 ,
                          tags : 1
                          }},
                        { $unwind : "$tags" }
                     );

 o/p:
{ "_id" : 1, "title" : "this is my title", "author" : "bob", "tags" : "fun" }
{ "_id" : 2, "title" : "this is my title", "author" : "bob", "tags" : "good" }
{ "_id" : 3, "title" : "this is my title", "author" : "bob", "tags" : "fun" }`.trim()

const chaining = `
db.customers.find().limit(2).sort({ title: 1 }).pretty()

Foreach
db.customers.find().forEach(doc=>{
  print('name',doc.First_Name)
