import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';


const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})

const commonArray = ``.trim()


const Array = `
var app = angular.module('myApp', []);
app.controller('myCtrl', timeCtrl);

var names = [];
 function timeCtrl ($scope, $http){
   $http({
     method:'get',
     url:'https://jsonplaceholder.typicode.com/users'
   }).then(res => {
    $scope.person = res.data;
    $scope.person.map(data => {
      names.push(data.id)
    })
    let scores = names.find(element => element > 4);
    let filterScores = names.filter(element => element > 4);
    let joinArr = filterScores.join("*");
    let spliceArr = filterScores.splice(0,2)
    let concatArr = filterScores.concat(scores,joinArr,"100");

    //The slice() method slices out a piece of an array into a new array.
    let sliceArr = filterScores.slice(0,2);
    let toStringArr = names.toString()
    let position = filterScores.indexOf(8)

    //The includes() method determines whether an array contains a specified element.
    let includeArr = filterScores.includes(10)
    console.log('find',includeArr)
  });
}
`.trim();


class Logic3 extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0)
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4><Sidebar /></h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>Common element in arrays</h3>
              <div style={titles}>
                <PrismCode
                  code={commonArray}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>Common element in strings</h3>
              <div style={titles}>
                <PrismCode
                  code={commonStr}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>convert an Array to Object</h3>
              <div style={titles}>
                <PrismCode
                  code={arrObj}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>Convert an Object into an Array </h3>
              <div style={titles}>
                <PrismCode
                  code={objArr}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>Sum of all elements of a given array</h3>
              <div style={titles}>
                <PrismCode
                  code={sumAll}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>Dispay dot value</h3>
              <div style={titles}>
                <PrismCode
                  code={dotVal}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>Last match name</h3>
              <div style={titles}>
                <PrismCode
                  code={lastMatch}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>Apply deep logic</h3>
              <b>functions.js</b>
              <div style={titles}>
                <PrismCode
                  code={functions}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <b>functions.html</b>
              <div style={titles}>
                <PrismCode
                  code={functionsH}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>Add data in empty array from api values</h3>
              <div style={titles}>
                <PrismCode
                  code={empty}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <h3>Pick a record from list of records</h3>
              <div style={titles}>
                <PrismCode
                  code={Pick}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <h3>Add sum/avg of number from a list</h3>
              <div style={titles}>
                <PrismCode
                  code={sum}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <h3>Avrage and keys/valuue pair extract from an object</h3>
              <div style={titles}>
                <PrismCode
                  code={object}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>Array methods</h3>
              <div style={titles}>
                <PrismCode
                  code={Array}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
            </List>
          </Paper>
        </Grid>
      </Grid>
    )
  }
}

export default (withStyles(styles)(Logic3));
