import React, { Component } from "react";
import Prism from "prismjs";
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import "../../ReactJs/styles.css";
import Sidebar from "../sidebar";
import PrismCode from "../prismCode";

const titles = { backgroundColor: "#F0F8FF", padding: "1px", fontSize: "16px" };

const styles = (theme) => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  actionDiv: {
    textAlign: "center",
  },
});

const slideshow = `
import "./slideshow.css";

const colors = ["green", "blue", "yellow"];
const delay = 2500;

export default function Slideshow() {
  const [index, setIndex] = useState(0);
  const timeoutRef = useRef(null);

  function resetTimeout() {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
  }

  useEffect(() => {
    resetTimeout();
    timeoutRef.current = setTimeout(() => setIndex((prevIndex) =>
          prevIndex === colors.length - 1 ? 0 : prevIndex + 1
        ), delay);

    return () => {
      resetTimeout();
    };
  }, [index]);

  return (
    <div className="slideshow">
      <div
        className="slideshowSlider"
        style={{ transform: 'translate3d('$'{-index * 100}%, 0, 0)' }}
      >
        {colors.map((backgroundColor, index) => (
          <div className="slide" key={index} style={{ backgroundColor }}></div>
        ))}
      </div>

      <div>
        {colors.map((_, idx) => (
          <div
            className={'slideshowDot'$'{index === idx ? " active" : ""}'}
            onClick={() => { setIndex(idx) }} />
        ))}
      </div>
    </div>
  );
}`.trim();

const slideshowCss = `
.slideshow {
  margin: 0 auto;
  overflow: hidden;
  max-width: 500px;
}

.slideshowSlider {
  white-space: nowrap;
  transition: ease 1000ms;
}

.slide {
  display: inline-block;

  height: 400px;
  width: 100%;
  border-radius: 40px;
}

.slideshowDots {
  text-align: center;
}

.slideshowDot {
  display: inline-block;
  height: 20px;
  width: 20px;
  border-radius: 50%;
  background-color: #c4c4c4;
}

.slideshowDot.active {
  background-color: #6a0dad;
}`.trim();

const fitersdata = `
const data = [
  { category: "vehicle", name: "car" },
  { category: "vehicle", name: "jeep" },
  { category: "fruit", name: "apple" },
  { category: "fruit", name: "orange" },
  { category: "electronics", name: "mobile" },
  { category: "electronics", name: "laptop" },
];

const App = () => {
  const [category, setCategory] = useState("vehicle");
  const filteredNames = data
    .filter((item) => item.category === category)
    .map((item) => item.name);

  return (
    <div>
      <label>
        Select a category:
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="vehicle">Vehicle</option>
          <option value="fruit">Fruit</option>
          <option value="electronics">Electronics</option>
        </select>
      </label> 
      <input
        type="text"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      />
      <ul>
        {filteredNames.map((name) => (
          <li key={name}>{name}</li>
        ))}
      </ul>
    </div>
  );
};
`.trim();

const progressbar = `
const App = () => {
  const [progress, setProgress] = useState(0);

  const handleClick = () => {
    if (progress < 100) {
      setProgress(progress + 10);
    }
  };

  const progressStyle = {
    width: '$'{progress}%,
    height: "20px",
    backgroundColor: "green",
  };

  return (
    <div>
      <div style={progressStyle}>{progress}%</div>
      <button onClick={handleClick}>Progress</button>
    </div>
  );
};
`.trim();

const mouseHovers = `
const App = () => {
  const [isHovering, setHovering] = useState(false);

  const handleMouseHover = () => {
    setHovering(isHovering => !isHovering);
  }

  return (
    <div>
      <div
        onMouseEnter={handleMouseHover}
        onMouseLeave={handleMouseHover}
      >
        Hover Me
      </div>
      {isHovering && <div>Hovering right me</div>}
    </div>
  );
}`.trim();

const MoveButton = `
const MoveButton = (props) => {
  return (
      <button onClick={props.onClick}>
          Click To Move
      </button>
  );
}

const BoxOne = () => <p>Box1</p>;

const BoxTwo = () => <p>Box2</p>;


class App extends Component {
state = { positions: 0 }

handleClick = () => {
  this.setState({ positions: (this.state.positions + 1) % 3 })
}

render () { 
const positions = this.state.positions;
  return (
      <div>
          { positions === 0 ? <MoveButton onClick={this.handleClick}/> : ''}
          <BoxOne />
          { positions === 1 ? <MoveButton onClick={this.handleClick}/> : ''}
          <BoxTwo />
          { positions === 2 ? <MoveButton onClick={this.handleClick}/> : ''}
      </div>
  );
}
}

export default App;`.trim();

class TicGame extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0);
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4>
              <Sidebar />
            </h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>1. Filter/Dropdawn</h3>
              <div style={titles}>
                <PrismCode
                  code={fitersdata}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>2. Slideshow</h3>
              <div style={titles}>
                <PrismCode
                  code={slideshow}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <b>slideshow.css</b>
              <div style={titles}>
                <PrismCode
                  code={slideshowCss}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>

              <h3>5. Progress incrase upto 100%</h3>
              <div style={titles}>
                <PrismCode
                  code={progressbar}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>5. Mouse Hover on Card</h3>
              <div style={titles}>
                <PrismCode
                  code={mouseHovers}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>6.onClick move button</h3>
              <div style={titles}>
                <PrismCode
                  code={MoveButton}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>4. Sort items on check box</h3>
              <div style={titles}>
                <PrismCode
                  code={sortItems}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
            </List>
          </Paper>
        </Grid>
      </Grid>
    );
  }
}

export default withStyles(styles)(TicGame);
