import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';

import Stcksval from '../../../assets/stcks.png';

const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const redesign = {
  height: 200,
  width: 500
}

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})


const rotate = `
const rotate = (matrix = []) => {
  const n = matrix.length
  for (let i = 0; i < Math.floor(n / 2); i++) {
    const temp = matrix[i]
    matrix[i] = matrix[n - 1 - i]
    matrix[n - 1 - i] = temp
  }
  for (let i = 0; i < n; i++) {
    for (let j = i + 1; j < n; j++) {
      const temp = matrix[i][j]
      matrix[i][j] = matrix[j][i]
      matrix[j][i] = temp
    }
  }
  return matrix
}

console.log(rotate([[1,2,3],[4,5,6],[7,8,9]]))`.trim();

const groupAnagrams = `
const groupAnagrams = function (strs) {
  const sortStr = str => str.split('').sort((a, b) => a.localeCompare(b)).join('')
  const sortedMap = strs.map(sortStr).reduce(
    (map, current, index) => {
      if (map[current]) {
        map[current].push(strs[index])
      } else {
        map[current] = [strs[index]]
      }
      return map
    },
    {},
  )
  return Object.keys(sortedMap).map(key => sortedMap[key])
}

console.log(groupAnagrams(["eat","tea","tan","ate","nat","bat"]))`.trim();

const myPow = `
const myPow = (x, n) => {
  if (n === 0) {
    return 1
  }
  const temp = myPow(x, Math.floor(Math.abs(n / 2)))
  const result = n % 2 === 0 ? temp * temp : x * temp * temp
  return n < 0 ? 1 / result : result
}

console.log(myPow(2.00000, 10))`.trim();

const maxSubArray = `
const maxSubArray = function (nums) {
  let max = -Infinity
  let currentMax = -Infinity
  for (let i = 0; i < nums.length; i++) {
    currentMax = Math.max(
      currentMax + nums[i],
      nums[i],
    )
    max = Math.max(max, currentMax)
  }
  return max
}

console.log(maxSubArray([-2,1,-3,4,-1,2,1,-5,4]))`.trim();

const spiralOrder = `
var spiralOrder = function(matrix) {
  let result = []
  if(!matrix.length) {
      return result
  }
  let rowMin = 0
  let rowMax = matrix.length -1
  let columnMin = 0
  let columnMax = matrix[0].length - 1

  let i = 0
  let j = 0
  let direction = "right"
  while(result.length < matrix.length * matrix[0].length) {

      result.push(matrix[i][j])
      if(direction === "right") {

          if(j === columnMax) {
              rowMin += 1
              i = rowMin
              direction = "down"
          } else {
              j++
          }
      } else if(direction === "down") {
          if(i === rowMax) {
              direction = "left"
              columnMax -= 1
              j = columnMax
          } else {
              i++
          }
      } else if(direction === "left") {

          if(j === columnMin) {
              direction = "up"
              rowMax -= 1
               i = rowMax
          } else {
              j--
          }
      } else {
          if(i === rowMin) {
              direction = "right"
              columnMin += 1
               j = columnMin
          } else {
              i--
          }
      }
  }
  return result 
};


console.log(spiralOrder([
  [ 1, 2, 3 ],
  [ 4, 5, 6 ],
  [ 7, 8, 9 ]
 ]))`.trim();

const uniquePaths = `
const uniquePaths = (m, n) => {
  const memo = {}
  const aux = (rowIndex, columnIndex) => {
    if (memo[rowIndex] !== undefined && memo[rowIndex][columnIndex] !== undefined) {
      return memo[rowIndex][columnIndex]
    }
    if (rowIndex >= m || columnIndex >= n) {
      return 0
    }
    if (rowIndex === m - 1 && columnIndex === n - 1) {
      return 1
    }
    memo[rowIndex] = memo[rowIndex] || {}
    memo[rowIndex][columnIndex] = aux(rowIndex + 1, columnIndex) + aux(rowIndex, columnIndex + 1)
    return memo[rowIndex][columnIndex]
  }
  return aux(0, 0)
}

console.log(uniquePaths(7,3))`.trim();

const plusOne = `
const plusOne = (digits = []) => {
  if (digits.length === 0) {
    return digits
  }
  if (digits[digits.length - 1] < 9) {
    digits[digits.length - 1] += 1
    return digits
  }
  let index = digits.length - 1
  let current = 1
  while (index >= 0) {
    const sum = current + digits[index]
    if (sum > 9) {
      digits[index] = sum - 10
      current = 1
      index -= 1
    } else {
      digits[index] = sum
      current = 0
      index -= 1
      return digits
    }
  }
  return current === 1 ? [1, ...digits] : digits
}


console.log(plusOne([1,2,3]))`.trim();

const addBinary = `
const addBinary = function (a, b) {
  const reverse = x => x.split('').reverse()
  const { shorter, longer } = a.length > b.length
    ? { shorter: reverse(b), longer: reverse(a) }
    : { shorter: reverse(a), longer: reverse(b) }

  let reminder = 0
  const digits = longer.map((num1, index) => {
    let res = parseInt(num1, 10) + reminder
      + (parseInt(shorter[index], 10) ? parseInt(shorter[index], 10) : 0)
    if (res >= 2) {
      res -= 2
      reminder = 1
    } else {
      reminder = 0
    }
    return res
  })
  if (reminder === 1) {
    digits.push(reminder)
  }
  return digits.reverse().join('')
}

console.log(addBinary("1010", "1011"))`.trim();

const mySqrt = `
const mySqrt = (x) => {
  let r = x
  while (r * r > x) {
    r = Math.floor((r + x / r) / 2)
  }

  return r
}

console.log(mySqrt(4))`.trim();

const climbStairs = `
const climbStairs = (n) => {
  const memo = {}
  const aux = (count = 0) => {
    if (memo[count] !== undefined) {
      return memo[count]
    }
    if (count === n) {
      return 1
    }
    if (count > n) {
      return 0
    }
    memo[count] = aux(count + 1) + aux(count + 2)
    return memo[count]
  }
  return aux()
}

console.log(climbStairs(3))`.trim();


class DSLogic3 extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0)
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4><Sidebar /></h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>1. Rotate Image.</h3>
              You are given an n x n 2D matrix representing an image, rotate the image by 90 degrees (clockwise).
<br/>
You have to rotate the image in-place, which means you have to modify the input 2D matrix directly. DO NOT allocate another 2D matrix and do the rotation.
<br/><br/>Input: matrix = [[1,2,3],[4,5,6],[7,8,9]]<br/>
Output: [[7,4,1],[8,5,2],[9,6,3]]
              <div style={titles}>
                <PrismCode
                  code={rotate}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>2. Group Anagrams.</h3>
              Given an array of strings strs, group the anagrams together. You can return the answer in any order.
              <br/>
An Anagram is a word or phrase formed by rearranging the letters of a different word or phrase, typically using all the original letters exactly once.
<br/> <br/>
Input: strs = ["eat","tea","tan","ate","nat","bat"]<br/>
Output: [["bat"],["nat","tan"],["ate","eat","tea"]]
              <div style={titles}>
                <PrismCode
                  code={groupAnagrams}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>3. Pow(x, n).</h3>
              Input: x = 2.00000, n = 10<br/>
Output: 1024.00000
              <div style={titles}>
                <PrismCode
                  code={myPow}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>4. Maximum Subarray.</h3>
              Input: nums = [-2,1,-3,4,-1,2,1,-5,4]<br/>
Output: 6<br/>
Explanation: [4,-1,2,1] has the largest sum = 6.
              <div style={titles}>
                <PrismCode
                  code={maxSubArray}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>5. Spiral Matrix.</h3>
              Input:
[<br/>
 [ 1, 2, 3 ],<br/>
 [ 4, 5, 6 ],<br/>
 [ 7, 8, 9 ]<br/>
]<br/><br/>
Output: [1,2,3,6,9,8,7,4,5]
              <div style={titles}>
                <PrismCode
                  code={spiralOrder}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>6. Unique Paths.</h3>
              A robot is located at the top-left corner of a m x n grid (marked 'Start' in the diagram below).
              <br/>
The robot can only move either down or right at any point in time. The robot is trying to reach the bottom-right corner of the grid (marked 'Finish' in the diagram below).
<br/>
How many possible unique paths are there? <br/> <br/>
Input: m = 7, n = 3<br/>
Output: 28
              <div style={titles}>
                <PrismCode
                  code={uniquePaths}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>7. Plus One.</h3>
              Input: digits = [1,2,3]<br/>
Output: [1,2,4]<br/>
<br/><br/>
Example 2:<br/>
Input: digits = [4,3,2,1]<br/>
Output: [4,3,2,2]
              <div style={titles}>
                <PrismCode
                  code={plusOne}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>8. Add Binary</h3>
              Given two binary strings, return their sum (also a binary string).
              <br/>
The input strings are both non-empty and contains only characters 1 or 0.<br/>
Input: a = "1010", b = "1011"<br/>
Output: "10101"
              <div style={titles}>
                <PrismCode
                  code={addBinary}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>9. Sqrt(x).</h3>
              Input: 4<br/>
Output: 2<br/><br/>
Example 2:<br/>

Input: 8<br/>
Output: 2<br/>
Explanation: The square root of 8 is 2.82842..., and since 
             the decimal part is truncated, 2 is returned.

              <div style={titles}>
                <PrismCode
                  code={mySqrt}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>10. Climbing Stairs.</h3>
              You are climbing a stair case. It takes n steps to reach to the top.<br/>

Each time you can either climb 1 or 2 steps. In how many distinct ways can you climb to the top?<br/>

Example 1:<br/>

Input: 2<br/>
Output: 2<br/>
Explanation: There are two ways to climb to the top.<br/>
1. 1 step + 1 step<br/>
2. 2 steps<br/><br/>
Example 2:<br/>

Input: 3<br/>
Output: 3<br/>
Explanation: There are three ways to climb to the top.<br/>
1. 1 step + 1 step + 1 step<br/>
2. 1 step + 2 steps<br/>
3. 2 steps + 1 step

              <div style={titles}>
                <PrismCode
                  code={climbStairs}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
            </List>
          </Paper>
        </Grid>
      </Grid>
    )
  }
}

export default (withStyles(styles)(DSLogic3));
