import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';

import Stcksval from '../../../assets/stcks.png';

const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const redesign = {
  height: 200,
  width: 500
}

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})


const combine = `
const combine = function (n, k) {
  const result = []
  const aux = (index = 1, current = []) => {
    if (current.length === k) {
      result.push(current)
      return
    }
    if (index > n) {
      return
    }
    aux(index + 1, [...current, index])
    aux(index + 1, current)
  }
  aux()
  return result
}

console.log(combine(4,2))`.trim();

const sortedArrayToBST = `
const sortedArrayToBST = (nums = []) => {
  const aux = (low, high) => {
    if (low <= high) {
      const middle = Math.floor((low + high) / 2)
      const node = {
        val: nums[middle],
      }
      node.left = aux(low, middle - 1)
      node.right = aux(middle + 1, high)
      return node
    }
    return null
  }
  return aux(0, nums.length - 1)
}

console.log(sortedArrayToBST([-10,-3,0,5,9]))`.trim();

const maxProfit = `
const maxProfit = function (prices) {
  let max = 0
  let minPrice = Infinity
  for (const price of prices) {
    minPrice = Math.min(price, minPrice)
    max = Math.max(max, price - minPrice)
  }
  return max
}

console.log(maxProfit([7,1,5,3,6,4]))`.trim();

const maxProduct = `
const maxProduct = (nums = []) => {
  if (nums.length === 0) {
    return 0
  }
  let prevMax = nums[0]
  let prevMin = nums[0]
  let max = nums[0]
  for (let i = 1; i < nums.length; i++) {
    const currentMax = Math.max(nums[i], prevMax * nums[i], prevMin * nums[i])
    const currentMin = Math.min(nums[i], prevMax * nums[i], prevMin * nums[i])
    prevMax = currentMax
    prevMin = currentMin
    if (prevMax > max) {
      max = prevMax
    }
  }
  return max
}

console.log(maxProduct([2,3,-2,4]))`.trim();






class DSLogic6 extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0)
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4><Sidebar /></h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>1. Combinations</h3>
              Given two integers n and k, return all possible combinations of k numbers out of 1 ... n.
              <br/>
You may return the answer in any order.<br/><br/>
Example 1:<br/>

Input: n = 4, k = 2<br/>
Output:
[<br/>
  [2,4],<br/>
  [3,4],<br/>
  [2,3],<br/>
  [1,2],<br/>
  [1,3],<br/>
  [1,4],<br/>
]<br/><br/>
Example 2:<br/>

Input: n = 1, k = 1<br/>
Output: [[1]]
              <div style={titles}>
                <PrismCode
                  code={combine}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>2. Convert Sorted Array to Binary Search Tree.</h3>
              Given an array where elements are sorted in ascending order, convert it to a height balanced BST.
              <br/>
For this problem, a height-balanced binary tree is defined as a binary tree in which the depth of the two subtrees of every node never differ by more than 1.
<br/><br/>
Given the sorted array: [-10,-3,0,5,9]<br/>
One possible answer is: [0,-3,9,-10,null,5]
              <div style={titles}>
                <PrismCode
                  code={sortedArrayToBST}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>3. Best Time to Buy and Sell Stock.</h3>
              Say you have an array for which the ith element is the price of a given stock on day i.
              <br/>
If you were only permitted to complete at most one transaction (i.e., buy one and sell one share of the stock), design an algorithm to find the maximum profit.
<br/>
Note that you cannot sell a stock before you buy one.<br/><br/>
Input: [7,1,5,3,6,4]<br/>
Output: 5<br/>
Explanation: Buy on day 2 (price = 1) and sell on day 5 (price = 6), profit = 6-1 = 5.
             Not 7-1 = 6, as selling price needs to be larger than buying price.
              <div style={titles}>
                <PrismCode
                  code={maxProfit}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>



              <h3>8. Maximum Product Subarray.</h3>
              Given an integer array nums, find the contiguous subarray within an array (containing at least one number) which has the largest product.
              <br/>
              Input: [2,3,-2,4]<br/>
Output: 6
              <div style={titles}>
                <PrismCode
                  code={maxProduct}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <h3>1. Longest Common Prefix</h3>
              Write a function to find the longest common prefix string amongst an array of strings.
<br/>
If there is no common prefix, return an empty string "".
<br/><br/>
Example 1:<br/>

Input: ["flower","flow","flight"]<br/>
Output: "fl"<br/><br/>
Example 2:<br/>

Input: ["dog","racecar","car"]<br/>
Output: ""<br/>
Explanation: There is no common prefix among the input strings.<br/><br/>
<b>N: </b>All given inputs are in lowercase letters a-z.
              <div style={titles}>
                <PrismCode
                  code={longestCommonPrefix}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>
            </List>
          </Paper>
        </Grid>
      </Grid>
    )
  }
}

export default (withStyles(styles)(DSLogic6));
