import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';

import Stcksval from '../../../assets/stcks.png';

const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const redesign = {
  height: 200,
  width: 500
}

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})


const combine = `
function combine(n, k) {
  const result = [];

  function aux(index = 1, current = []){
    if (current.length === k) {
      result.push(current);
      return;
    }
    if (index > n) return;

    aux(index + 1, [...current, index]);
    aux(index + 1, current);
  }
  aux();
  return result;
}

console.log(combine(4,2))
`.trim();

const maxProfit = `
function maxProfit(prices) {
  let max = 0;
  let minPrice = Infinity;

  for (const price of prices) {
    minPrice = Math.min(price, minPrice);
    max = Math.max(max, price - minPrice);
  }
  return max;
}

console.log(maxProfit([7,1,5,3,6,4]))
`.trim();

const maxProduct = `
function maxProduct(nums = []){
  if (nums.length === 0) return 0;

  let prevMax = nums[0];
  let prevMin = nums[0];
  let max = nums[0];

  for (let i = 1; i < nums.length; i++) {
    const currentMax = Math.max(nums[i], prevMax * nums[i], prevMin * nums[i]);
    const currentMin = Math.min(nums[i], prevMax * nums[i], prevMin * nums[i]);
    prevMax = currentMax;
    prevMin = currentMin;

    if (prevMax > max) {
      max = prevMax;
    }
  }
  return max;
}

console.log(maxProduct([2,3,-2,4]))
`.trim();

const longestCommonPrefix = `

`.trim();

