import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';

import Stcksval from '../../../assets/stcks.png';

const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const redesign = {
  height: 200,
  width: 500
}

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})


const combine = `
function combine(n, k) {
  const result = [];

  function aux(index = 1, current = []){
    if (current.length === k) {
      result.push(current);
      return;
    }
    if (index > n) return;

    aux(index + 1, [...current, index]);
    aux(index + 1, current);
  }
  aux();
  return result;
}

console.log(combine(4,2))
`.trim();

const maxProfit = `
function maxProfit(prices) {
  let max = 0;
  let minPrice = Infinity;

  for (const price of prices) {
    minPrice = Math.min(price, minPrice);
    max = Math.max(max, price - minPrice);
  }
  return max;
}

console.log(maxProfit([7,1,5,3,6,4]))
`.trim();

const maxProduct = `
function maxProduct(nums = []){
  if (nums.length === 0) return 0;

  let prevMax = nums[0];
  let prevMin = nums[0];
  let max = nums[0];

  for (let i = 1; i < nums.length; i++) {
    const currentMax = Math.max(nums[i], prevMax * nums[i], prevMin * nums[i]);
    const currentMin = Math.min(nums[i], prevMax * nums[i], prevMin * nums[i]);
    prevMax = currentMax;
    prevMin = currentMin;

    if (prevMax > max) {
      max = prevMax;
    }
  }
  return max;
}

console.log(maxProduct([2,3,-2,4]))
`.trim();

const longestCommonPrefix = `
function fun() {
  const candies = 10;
  const numChildren = 3;
  const result = [];
  for (let i = 0; i < numChildren; i++) {
      result[i] = 0;
  }

  let remainingCandies = candies;
  let nextCandy = 1;
  let currentIndex = 0;

  while (remainingCandies > 0) {
      result[currentIndex] += Math.min(nextCandy, remainingCandies);
      remainingCandies -= nextCandy;
      nextCandy++;
      currentIndex = (currentIndex + 1) % numChildren;
  }

  return console.log(result);
}

fun();
`.trim();

const dictionary = `
function fun() {
  const words = ["hello", "leetcode"];
  const order = "hlabcdefgijkmnopqrstuvwxyz";

  for (let i = 1; i < words.length; i++) {
    const prevWord = words[i - 1];
    const currentWord = words[i];

    let j = 0;
    while (j < prevWord.length && j < currentWord.length) {
      const prevCharIndex = order.indexOf(prevWord[j]);
      const currentCharIndex = order.indexOf(currentWord[j]);

      if (prevCharIndex < currentCharIndex) {
        break;                    // Correct order, move to the next pair of words
      } else if (prevCharIndex > currentCharIndex) {
        console.log('False');     // Incorrect order
        return;
      } else {
        j++;                      // Characters are equal, move to the next character
      }
    }

    if (j === currentWord.length && j < prevWord.length) {
      console.log('False');       // Incorrect order
      return;
    }
  }

  console.log('True'); // All pairs are in correct order
}

fun();
`.trim();

// const longestCommonPrefix = `

// `.trim();

