function star(){
  let n = 3;
  let str = "";
  for (let i = 1; i <= n; i++){
    for (let j=0; j<(n-i); j++){
      str += " ";
    }
    for (let k=0; k<i; k++){
      str += "*";
    }
    str += "newLine";
  }
console.log(str);
}
star();

