function star(){
  let n = 3;
  let str = "";
  for (let i = n; i >= 1; i--){
    for (let j=0; j<(n-i); j++){
      str += " ";
    }
    for (let k=0; k<i; k++){
      str += "*";
    }
    str += "newLine";
  }
console.log(str);
}
star();
`.trim();

