// Upside pyramid.
function pyraminds() {
  let i, j, k, str = "";

  for (i=0; i<5; i++) {
    for (j=1; j<(5 - i); j++) {
      str += " ";
    }
    for (k=1; k<=(2 * i +1); k++) {
      str += "*";
    }

    str += 'newLine';
  }
  console.log(str)
}

pyraminds();


// downside pyramid.
for (i=1; i<5; i++) {
  for (j=0; j<i; j++) {
    str += " ";
  }
  for (k=(5 - i)*2; k>1; k--) {
    str += "*";
  }

  str += 'newLine';
}
console.log(str)
