import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';

import Stcksval from '../../../assets/stcks.png';

const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const redesign = {
  height: 200,
  width: 500
}

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})


const factorial = `
var factorial = function(n){
  if(n === 1) return 1
  return n * factorial(n-1)
 }

 console.log(factorial(4));
 `.trim()

const prime = `
 function prime(){
   let i, j, result=[];
   for(i=0; i<100; i++){
     var count=0
     for(j=2; j<i; j++){
       if(i%j == 0){
         count += 1;
       }
     }
     if(count==0){
       result.push(i);
     }
   }

   console.log(result);
 }

 prime();
 `.trim()

const countPrimes = `
 const countPrimes = (n) => {
   const map = []
   for (let i = 2; i < n; i++) {
     map[i] = true
   }
   for (let i = 2; i * i < n; i++) {
     if (map[i]) {
       for (let j = i * i; j < n; j += i) {
         map[j] = false
       }
     }
   }
   return map.filter(num => num === true).length
 }

 console.log(countPrimes(10))`.trim();

const magicNumber = `
 function magicNum(){
   const random = Math.random(0,1)
   return random;
 }

 function main(){
   const result = Math.random(0, magicNum);
   console.log(result);
 }

 main();
 `.trim();

const possible = `
function isInt(value) {
  var x;
  if (isNaN(value)) {
    return false;
  }
  x = parseFloat(value);
  return (x | 0) === x;
}

console.log(isInt(0));`.trim();



const common_divisor = `
function numbers(x, y) {
    if ((typeof x !== 'number') || (typeof y !== 'number'))
      return false;
      x = Math.abs(x);
      y = Math.abs(y);
        while(y) {
          var i = y;
          y = x % y;
          x = i;
        }
  return x;
  }

  console.log(numbers(12, 4));
  console.log(numbers(9, 3));`.trim()

const addTwoNumbers = `
function ListNode(val) {
  this.val = val
  this.next = null
}

const addTwoNumbers = (l1, l2) => {
  let h1 = l1
  let h2 = l2
  let digit = 0
  let head = null
  let current = null
  while (h1 || h2) {
    const num1 = h1 ? h1.val : 0
    const num2 = h2 ? h2.val : 0
    let value = num1 + num2 + digit
    if (value >= 10) {
      value -= 10
      digit = 1
    } else {
      digit = 0
    }
    h1 = h1 ? h1.next : null
    h2 = h2 ? h2.next : null
    const node = new ListNode(value)
    if (!head) {
      head = node
      current = node
    } else {
      current.next = node
      current = node
    }
  }
  if (digit === 1) {
    current.next = new ListNode(1)
  }
  return head
}

console.log(addTwoNumbers())`.trim();

const reverse = `
const reverse = function (x) {
  let max = (2 ** 31) - 1
  let min = - (2 ** 31)

  const isMinus = x < 0
  const str = String(Math.abs(x)).split('').reverse().join('')
  const result =  isMinus ? Number('-'$'{str}') : Number(str)
  if (result > max || result < min) {
    return 0
  }
  return result
}

console.log(reverse(123))`.trim();

const brokenCalc = `
const brokenCalc = function (X, Y) {
  let result = 0
  while (Y > X) {
    if (Y % 2 === 0) {
      Y /= 2
    } else {
      Y += 1
    }
    result += 1
  }
  return result + X - Y
}

console.log(brokenCalc(5, 8))`.trim();

const addStrings = `
const addStrings = (num1, num2) => {
  const reversedNum1 = num1.split('').reverse()
  const reversedNum2 = num2.split('').reverse()
  const { shorter, longer } = reversedNum1.length < reversedNum2.length
    ? { shorter: reversedNum1, longer: reversedNum2 }
    : { shorter: reversedNum2, longer: reversedNum1 }

  let rem = 0
  let result = longer.reduce((acc, digit1, index) => {
    const total = parseInt(digit1, 10) + (parseInt(shorter[index], 10) || 0) + rem
    if (total >= 10) {
      rem = 1
    } else {
      rem = 0
    }
    return [
      ...acc,
      total >= 10 ? total - 10 : total,
    ]
  }, [])
  result = rem === 1 ? [...result, rem] : result
  return result.reverse().join('')
}

const zero = (count) => {
  let result = ''
  while (count >= 1) {
    result += '0'
    count -= 1
  }
  return result
}
const multiply = (num1 = '', num2 = '') => {
  let result = 0
  for (let i = num2.length - 1; i >= 0; i--) {
    let current = ''
    let base = 0
    for (let j = num1.length - 1; j >= 0; j--) {
      let currentResult = parseInt(num1[j], 10) * parseInt(num2[i], 10) + base
      if (currentResult >= 10) {
        base = Math.floor(currentResult / 10)
        currentResult -= base * 10
      } else {
        base = 0
      }
      current = '$'{currentResult + current}'
    }
    if (base > 0) {
      current = base + current
    }
    // 偷懒
    result = addStrings(result || '0', current + zero(num2.length - 1 - i))
  }
  if (result.length > 0 && result[0] === '0') {
    return '0'
  }
  return result
}


console.log(addStrings("2","3"))`.trim();




const mySqrt = `
const mySqrt = (x) => {
  let r = x
  while (r * r > x) {
    r = Math.floor((r + x / r) / 2)
  }

  return r
}

console.log(mySqrt(4))`.trim();

const myPow = `
const myPow = (x, n) => {
  if (n === 0) {
    return 1
  }
  const temp = myPow(x, Math.floor(Math.abs(n / 2)))
  const result = n % 2 === 0 ? temp * temp : x * temp * temp
  return n < 0 ? 1 / result : result
}

console.log(myPow(2.00000, 10))`.trim();

const letterCombinations = `
const letterCombinations = (digits = '') => {
  const map = {
    2: ['a', 'b', 'c'],
    3: ['d', 'e', 'f'],
    4: ['g', 'h', 'i'],
    5: ['j', 'k', 'l'],
    6: ['m', 'n', 'o'],
    7: ['p', 'q', 'r', 's'],
    8: ['t', 'u', 'v'],
    9: ['w', 'x', 'y', 'z'],
  }
  if (!digits.length) {
    return []
  }
  let result = ['']
  let index = 0
  while (index < digits.length) {
    const next = map[digits[index]]
    const nextResult = []
    for (let i = 0; i < result.length; i++) {
      for (let j = 0; j < next.length; j++) {
        nextResult.push(result[i] + next[j])
      }
    }
    result = nextResult
    index++
  }
  return result
}

console.log(letterCombinations("23"))`.trim();

const removeNthFromEnd = `
const removeNthFromEnd = (head, n) => {
  let h1 = head
  let h2 = null
  let count = 0
  while (h1) {
    count += 1
    h1 = h1.next
    if (h2) {
      h2 = h2.next
    }
    if (count === n + 1) {
      h2 = head
    }
  }
  if (!h2) {
    return head ? head.next : null
  }
  h2.next = h2.next.next
  return head
}`.trim();

const isValid = `
const isValid = function (s) {
  const stack = []
  const pair = {
    '[': ']',
    '{': '}',
    '(': ')',
  }
  const isPaired = (a, b) => pair[a] === b

  for (c of s) {
    if (isPaired(stack[stack.length - 1], c)) {
      stack.pop()
    } else {
      stack.push(c)
    }
  }
  return stack.length === 0
}

console.log(isValid("()"))`.trim();

const mergeTwoLists = `
const mergeTwoLists = (l1, l2) => {
  const aux = (current1, current2, acc) => {
    if (!current1) {
      acc.next = current2
      return acc
    }
    if (!current2) {
      acc.next = current1
      return acc
    }
    if (current1.val < current2.val) {
      acc.next = {
        val: current1.val,
        next: null,
      }
      return aux(current1.next, current2, acc.next)
    }
    acc.next = {
      val: current2.val,
      next: null,
    }
    return aux(current2.next, current1, acc.next)
  }
  const head = { val: null, next: null }
  aux(l1, l2, head)
    return head.next
}`.trim();

const generateParenthesis = `
const generateParenthesis = function (n) {
  const result = []
  const aux = (current, left, right) => {
    if (left === n && right === n) {
      result.push(current)
      return
    }
    if (left < right || left > n || right > n) {
      return
    }
    aux('$'{current}(', left + 1, right)
    aux('$'{current})', left, right + 1)
  }
  aux('', 0, 0)
  return result
}
`.trim();

const swap = `const swap = (a, b, arr) => { // eslint-disable-line
  if (a !== b) {
    const temp = arr[a]
    arr[a] = arr[b] // eslint-disable-line
    arr[b] = temp // eslint-disable-line
  }
}

const Heap = compareFn => (arr = []) => {
  const left = index => 2 * index + 1
  const right = index => 2 * index + 2
  const parent = index => Math.floor((index - 1) / 2)
  const size = () => arr.length

  // log(n)
  const heapify = (index) => {
    const l = left(index)
    const r = right(index)
    let current = index
    if ((l < size()) && compareFn(arr[current], arr[l]) > 0) {
      current = l
    }
    if ((r < size()) && compareFn(arr[current], arr[r]) > 0) {
      current = r
    }
    if (current !== index) {
      swap(current, index, arr)
      heapify(current)
    }
  }
  // log(n)
  const heapifyUp = (index) => {
    const p = parent(index)
    if (p >= 0 && compareFn(arr[p], arr[index]) > 0) {
      swap(p, index, arr)
      heapifyUp(p)
    }
  }
  // O(n)
  const buildHeap = () => {
    for (let i = Math.floor(arr.length / 2); i >= 0; i--) {
      heapify(i)
    }
  }
  const extract = () => {
    swap(0, arr.length - 1, arr)
    const top = arr.pop()
    heapify(0)
    return top
  }
  const remove = (item) => {
    const index = arr.findIndex(x => compareFn(x, item) === 0)
    if (index === -1) {
      return
    }
    arr[index] = arr.pop() // eslint-disable-line
    const p = parent(index)
    if (p < 0 || compareFn(p, arr[index]) < 0) {
      heapify(index)
    } else {
      heapifyUp(index)
    }
  }
  buildHeap()
  return {
    getHeap: () => arr,
    peek: () => {
      if (arr.length === 0) {
        return null
      }
      return arr[0]
    },
    add: (item) => {
      arr.push(item)
      heapifyUp(arr.length - 1)
    },
    extract,
    remove,
    size,
  }
}

