import React, { Component } from "react";
import Prism from "prismjs";
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import "../../ReactJs/styles.css";
import Sidebar from "../sidebar";
import PrismCode from "../../ReactJs/prismCode";

const titles = { backgroundColor: "#F0F8FF", padding: "1px", fontSize: "16px" };

const styles = (theme) => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  actionDiv: {
    textAlign: "center",
  },
});

const forChildFun = `
import { MyService } from './my.service';

@NgModule({
  imports: [ ... ],
  providers: [ MyService ]
})
export class MyModule {
  static forRoot(): ModuleWithProviders<MyModule> {
    return {
      ngModule: MyModule,
      providers: [ MyService ]
    };
  }
}`.trim();

const forRootFun = `
import { MyService } from './my.service';

@NgModule({
  imports: [ ... ]
})
export class MyFeatureModule {
  static forChild(): ModuleWithProviders<MyFeatureModule> {
    return {
      ngModule: MyFeatureModule,
      providers: [ MyService ]
    };
  }
}`.trim();

const interceptor = `
import { Injectable } from '@angular/core';
import { HttpInterceptor, HttpHandler, HttpRequest } from '@angular/common/http';

@Injectable()
export class AuthInterceptor implements HttpInterceptor {
  intercept(request: HttpRequest<any>, next: HttpHandler) {
    const authToken = localStorage.getItem('authToken');

    const authRequest = request.clone({
      headers: request.headers.set('Authorization', 'Bearer '$'{authToken}')
    });

    // Pass the auth request on to the next interceptor or to the HttpClient if there are no more interceptors
    return next.handle(authRequest);
  }
}
`.trim();

const transition = `
transition('open => closed', [
  animate('500ms')
]),`.trim();

const pipeFor = `
@Component({
  selector: 'app-birthday',
  template: '<p> Birthday is {{birthday | date}}</p>'
})
export class BirthdayComponent {
  birthday = new Date(1987, 6, 18); 
}`.trim();

const paraPipes = `
@Component({
      selector: 'app-birthday',
      template: '<p> Birthday is {{birthday | date: 'dd/MM/yyyy'}}</p>' // 18/06/1987
    })
    export class BirthdayComponent {
      birthday = new Date(1987, 6, 18);
    }`.trim();

const chainPipe = `
@Component({
          selector: 'app-birthday',
          template: '<p> Birthday is {{birthday | date: 'fullDate' | uppercase}} </p>' // THURSDAY, JUNE 18, 1987
        })
        export class BirthdayComponent {
          birthday = new Date(1987, 6, 18);
        }`.trim();

const purpose = `
@Component({
  selector: 'async-observable-pipe',
  template: '<div> <code>observable|async</code>:
  Time: {{time | async}}</div >'
})
export class AsyncObservablePipeComponent {
  time = new Observable(observer =>
    setInterval(() => observer.next(new Date().toString()), 2000)
  );
}`.trim();

const ViewContainerRef =
  `let componentRef = viewContainerRef.createComponent(componentFactory);`.trim();

const template = `
  <p>Hip!</p>
  <ng-template>
    <p>Hip!</p>
  </ng-template>
  <p>Hooray!</p>

  //<ng-container>
  @Component({
    selector: 'app-root',
    template: '
        <ng-template #loading>
          ng-template rendering
        </ng-template>

        <p>ng-container</p>
        <ng-container *ngIf="display else loading">
          ng-container rendering

        </ng-container>
        <ul>
            <li *ngFor="let emp of employees; let i=index">
              <ng-container *ngIf="emp.salery>10000">
                {{i+1}} - {{emp.name}}
              </ng-container>
            </li>
          </ul>
    '',
    providers:[]
  })

  export class AppComponent {
    display=false;

    employees = [
      {"name":"Mukesh", "salery":10000},
      {"name":"Rakesh", "salery":12000},
      {"name":"Nitesh", "salery":15000},
    ]
  }
  `.trim();

const typeFunc = `
  template:
    '{{ $any(user).contacts.email }}'


  //The $any() cast function also works with this to allow access to undeclared members of the component.
     template:
     '{{ $any(this).contacts.email }}'`.trim();

const assertionOpp = `@Component({
    selector: 'my-component',
    template: '<span *ngIf="user"> {{user.name}} contacted through {{contact!.email}} </span>'
  })
  class MyComponent {
    user?: User;
    contact?: Contact;

    setData(user: User, contact: Contact) {
      this.user = user;
      this.contact = contact;
    }
  }`.trim();

const narrowing = `
  @Component({
    selector: 'my-component',
    template: '<span *ngIf="user"> {{user.contact.email}} </span>'
  })
  class MyComponent {
    user?: User;
  }`.trim();

const multicasting = `
  var source = Rx.Observable.from([1, 2, 3]);
  var subject = new Rx.Subject();
  var multicasted = source.multicast(subject);

  // These are, under the hood, 'subject.subscribe({ ...})':
  multicasted.subscribe({
    next: (v) => console.log('observerA: ' + v)
  });
  multicasted.subscribe({
    next: (v) => console.log('observerB: ' + v)
  });`.trim();

const changeDetector = `
  import { ChangeDetectionStrategy, Component } from '@angular/core';

  @Component({
    selector: 'app-root',
    template: '
    <button (click)='updateName()'>Update</button>
    Welcome {{userValue.firstName}} - {{userValue.lastName}}
    <div *ngFor="let str of myStrArr">{{str}}</div>
    <br/>
    <p>onPush Stretgy</p>
    <button (click)='onClick()'>onPush</button>
    ',

    changeDetection:ChangeDetectionStrategy.OnPush
  })
  export class AppComponent {
    title = '3_change_detection';
    userValue:any;
    myStrArr:any[]=[];

    ngOnInit():void{
      this.userValue = {
        firstName:'Mukesh',
        lastName:'Singh'
      };
    }

    updateName(){
      this.userValue.firstName='Rakesh';
      console.log('default Stretgy')
    }

    onClick(){
      this.myStrArr.push('onPush Stretgy')
      console.log('onPush')
    }
  }
  `.trim();

const viewChilds = `
  //salery.ts
  @Component({
    selector: 'salery',
    template: '{{msg}}:{{empSalery}}',
    providers: [],
  })
  export class Salery {
    empSalery = 0;
    msg = '';

    saleryIncre() {
      this.empSalery += 500;
      this.msg = 'Incremented Salery';
    }

    saleryDecre() {
      this.empSalery -= 500;
      this.msg = 'Decremented Salery';
    }
  }


  //app.component.ts
  import { Component, ViewChild } from '@angular/core';
  import { Salery } from './salery';

  @Component({
    selector: 'app-root',
    template: '
      <button (click)="empIncreaseSalery()">Inc</button>
      <button (click)="empDecreSalery()">Din</button>
      <salery></salery>
    ',
  })
  export class AppComponent {
    @ViewChild(Salery)
    Salery!: Salery;

    empIncreaseSalery() {
      this.Salery.saleryIncre();
    }

    empDecreSalery() {
      this.Salery.saleryDecre();
    }
  }
  `.trim();

const viewChildsDir = `
  //salery.ts
  @Component({
    selector: 'app-salery',
    template: '<p>{{msg}}: {{empSalery}}</p>'',
  })
  export class SaleryComponent {
    empSalery=0;
    msg='';
    static saleryIncrement: any;

    saleryIncrement(){
        this.empSalery = this.empSalery + 500;
        this.msg = "Incremented Salery"
        console.log('Incremented');
    }

    saleryDec(){
      this.empSalery = this.empSalery - 500;
      this.msg = "Decremented Salery"
    }
  }


  //employees.ts
  import { Component, ViewChild, ElementRef, AfterViewInit } from '@angular/core';
  import { SaleryComponent } from '../salery/salery.component';

  @Component({
    selector: 'app-employees',
    template: '
      <p appEmpdirective>ViewChild using Directive Employees</p>
      <input type="text" name="username" #usernameRef /><br/>
      <input type="text" name="email" #usernameRef />'
    })
  export class EmployeesComponent implements AfterViewInit {

    @ViewChild('usernameRef') usernameRef!: ElementRef;

    ngAfterViewInit(): void {
      this.usernameRef.nativeElement.focus();
    }

    incremented(){
      // console.log('Incremented');
    }

    decremented(){
      console.log('Decremented');
    }
  }


  //empdirective.directive.ts
  import { Directive, ElementRef, AfterViewInit } from '@angular/core';

  @Directive({
    selector: '[appEmpdirective]'
  })
  export class EmpdirectiveDirective implements AfterViewInit{

    constructor(private eleRef:ElementRef) { }

    ngAfterViewInit(): void {
      this.eleRef.nativeElement.style.color="green";
    }
  }

  //app.ts
  import { Component } from '@angular/core';

  @Component({
    selector: 'app-root',
    template: '
        <p>ViewChild using Directive</p>
        <app-employees></app-employees>',
    providers:[]
  })

  export class AppComponent {
    display=false;

    employees = [
      {"name":"Mukesh", "salery":10000},
      {"name":"Rakesh", "salery":12000},
      {"name":"Nitesh", "salery":15000},
    ]
  }
  `.trim();

const viewChildsTemp = `
  //salery.ts
