import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../prismCode';


const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})

const refsReact = `
Problem:
this.setState({
  counter: this.state.counter + this.props.increment,
});

Answer:
this.setState((state, props) => ({
  counter: state.counter + props.increment
}));
`.trim();

const updateState = `
Problem: Have a component that receives a prop that looks like this:
const styles = {
  font: {
      size: {
          value: '22',
          unit: 'px'
      },
      weight: 'bold',
      color: '#663300',
      family: 'arial',
      align: 'center'
  }
};

How to update only the align property?

Answer: 
        const { ...styling } = styles;
        const [style, setStyle] = useState(styling);
        ...

        setStyle(prevStyle => ({
                ...prevStyle,
                font: { ...prevStyle.font, align: event.target.value }
            }));


`.trim();

const getLists = ``.trim();

// const getLists = ``.trim();

// const getLists = ``.trim();

// const getLists = ``.trim();

// const getLists = ``.trim();

// const getLists = ``.trim();

// const getLists = ``.trim();

// const getLists = ``.trim();


class CodingInter extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0)
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4><Sidebar /></h4>
          </Paper>
        </Grid>

        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>What's wrong with that code?</h3>
              <div style={titles}>
                <PrismCode
                  code={refsReact}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>
              <i>Because <b>this.props</b> and <b>this.state</b> may be updated asynchronously, you should not rely on their 
              values for calculating the next state. To fix it, use a second form of <b>setState()</b> that accepts a 
              function rather than an object. That function will receive the previous state as the first argument, and 
              the props at the time the update is applied as the second argument</i>
              <br/>

              <h3>How do I update state on a nested object with useState()?</h3>
              <div style={titles}>
                <PrismCode
                  code={updateState}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>

              <h3></h3>
              <br/>
            </List>
          </Paper>
        </Grid>
      </Grid>
    )
  }
}

export default (withStyles(styles)(CodingInter));
