import React, { Component } from "react";
import Prism from "prismjs";
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import "../../ReactJs/styles.css";
import Sidebar from "../sidebar";
import PrismCode from "../../ReactJs/prismCode";

const titles = { backgroundColor: "#F0F8FF", padding: "1px", fontSize: "16px" };

const styles = (theme) => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  actionDiv: {
    textAlign: "center",
  },
});


const insersonSort = `
function fun() {
  const arr = [3, 5, 1, 9, 6, 2, 1, -1];
  const hashSet = new Set(arr);
  const result = [...hashSet].sort((a, b) => a - b);
  console.log(result);
}

fun();
`.trim();

const mergSort = `
function mergeSort(arr){
    if(arr.length < 2) return arr;
    var middle = Math.floor(arr.length/2);
    var left = arr.slice(0, middle);
    var right = arr.slice(middle, arr.length);
    return merge(mergeSort(left), mergeSort(right));
 }

 function merge(left, right){
  var result = [];
    while(left.length && right.length){
      if(left[0] == right[0]){
        result.push(left.shift());
      }
  else{
    result.push(right.shift());}
  }

  while(left.length) result.push(left.shift());
  while(right.length) result.push(right.shift());
  console.log(result)
  return result;
 }
 mergeSort([3,2,1])`.trim();

const quicksort = `
function QuickSort(arr){
    if(arr.length <= 1) return arr;
        var pivot = arr[arr.length -1];
        var left = [];
        var right = [];
        for(var i=0;i<arr.length-1;i++){
        if(arr[i] < pivot){
        left.push(arr[i])
      }
    else right.push(arr[i])
    }
    return [...QuickSort(left), pivot, ...QuickSort(right)]
 }
 console.log(QuickSort([5,4,2,7,9]))
 `.trim();

const selectionSort = `function selectionSort(arr){
  var minIdx, temp,
  len = arr.length;
    for(var i = 0; i < len; i++){
    minIdx = i;
      for(var j = i+1; j<len; j++){
        if(arr[j]<arr[minIdx]){
        minIdx = j;
      }
    }

    temp = arr[i];
    arr[i] = arr[minIdx];
    arr[minIdx] = temp;
   }

 console.log(arr)
 return arr;
 }

 selectionSort([7,5,2,4,3,9]);`.trim();

const Permutations = `
`.trim();


const amount = `
function minCoin(amount, coins) {
  let count = 0;
  for (let i = 0; i < coins.length; i++) {
    while (coins[i] <= amount) {
      amount -= coins[i];
      count++;
    }
  }
  return console.log(count);
}

minCoin(87, [25, 10, 5, 1]);`.trim();

const armstrong = `function fun(num) {
  const newnum = num.toString().split('');
  const hash = newnum.reduce((sum, digit) => {
    const cube = Math.pow(parseInt(digit), newnum.length);
    return sum + cube;
  }, 0);

  if (hash === num) {
    return { type: 'Armstrong', hash: hash };
  } else {
    return { type: 'Not Armstrong', hash: hash };
  }
}

const hash = fun(153);
console.log(hash); `.trim(); 

const satisfy = `
function isEven(num) {
  return num % 2 === 0;
}

function isPrime(num) {
  if (num < 2) {
    return false;
  }
  for (let i = 2; i <= Math.sqrt(num); i++) {
    if (num % i === 0) {
      return false;
    }
  }
  return true;
}

// Function to loop through an array and check if n elements satisfy the condition function
function some(array, n, isCheck) {
  let count = 0;
  for (let i = 0; i < array.length; i++) {
    if (isCheck(array[i])) {
      count++;
    }
    if (count === n) {
      return true;
    }
  }
  return false;
}

console.log(some([2, 4, 6], 3, isEven)); // true
console.log(some([2, 3, 4], 3, isEven)); // false
console.log(some([2, 3, 11], 4, isPrime)); // false
console.log(some([2, 3, 5, 9], 3, isPrime)); // true`.trim();

const generates = `
function fibbo() {
  let a = 0;
  let b = 1;

  return function() {
    const result = a;
    const next = a + b;
    a = b;
    b = next;
    return result;
  };
}

let obj = fibbo();
console.log(obj()); // 0
console.log(obj()); // 1
console.log(obj()); // 1
console.log(obj()); // 2
console.log(obj()); // 3
console.log(obj()); // 5
console.log(obj()); // 8
`.trim();

class Logic2 extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0);
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4>
              <Sidebar />
            </h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>1. Armstrong Number</h3>
              <div style={titles}>
                <PrismCode
                  code={armstrong}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>5. Permutations</h3>
              <div style={titles}>
                <PrismCode
                  code={Permutations}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <b>
                6. Given an amount of money, return the minimum number of coins
                needed to make that change.
              </b>
              <div style={titles}>
                <PrismCode
                  code={amount}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>7. Sort an Array</h3>
              <div style={titles}>
                <PrismCode
                  code={insersonSort}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>8. Merg Sort</h3>
              <div style={titles}>
                <PrismCode
                  code={mergSort}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>9. Quick Sort</h3>
              <div style={titles}>
                <PrismCode
                  code={quicksort}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>10. Selection Sort</h3>
              <div style={titles}>
                <PrismCode
                  code={selectionSort}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br/>

              <b>11. Write a function which loops through an array and checks if n of the elements
                of the array satisfy the condition function that is passed</b>
                <br/>
                <br/>
                <i>Write the some function and isEven and isPrime functions</i>
                <br/>
                <br/>
                <ul>
                  <li>console.log(some([2,4,6], 3, isEven)) // should print true</li>
                  <li>console.log(some([2,3,4], 3, isEven)) // should print false</li>
                  <li>console.log(some([2,3,11], 4, isPrime)) // should print false</li>
                  <li>console.log(some([2,3,5,9], 3, isPrime)) // should print true</li>
                </ul>
              <div style={titles}>
                <PrismCode
                  code={satisfy}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>12. Write a function whch returns a function that generates fibonacci numbers.
Don't use generators.</h3>
              <div style={titles}>
                <PrismCode
                  code={generates}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
            </List>
          </Paper>
        </Grid>
      </Grid>
    );
  }
}

export default withStyles(styles)(Logic2);
