import React, { Component } from "react";
import Prism from "prismjs";
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import "../../ReactJs/styles.css";
import Sidebar from "../sidebar";
import PrismCode from "../../ReactJs/prismCode";

import EventLoop from "../../../assets/imgs.svg";

const titles = { backgroundColor: "#F0F8FF", padding: "1px", fontSize: "16px" };

const redesign = {
  height: 350,
  width: 600,
};

const styles = (theme) => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  actionDiv: {
    textAlign: "center",
  },
});

const undeclaredVar = `
var a
console.log('Undefined variables', a)
console.log('Undeclared variables', b)`.trim();

const varConst = `
var obj = Object.freeze({ name: 'Mukesh' });
obj.name = 'Rakesh';

console.log(obj.name);                                                  
`.trim();

const iifes = `
var x = 23; 
  (function(){   
      var x = 43;   
      (function random(){     
          x++;     
          console.log(x);     
          var x = 21;   
      })(); 
  })();                                                           
`.trim();

const spreadRest = `
//Rest
function add(a,b,c,...other){
  console.log(other);
  console.log(other[0]);
  return a+b+c;
}

console.log(add(3,4,5,1,2));


//2
const num = [1,2,3,4,5,6]
function addSpread(num, num2, num3){
  console.log(num, num2, num3);
}

// console.log(addSpread(num[0], num[1], num[2]));
addSpread(...num);
addSpread(num);
`.trim();

const currying = `
function sum(a){
  return function(b){
    return function(c){
      return a+b+c;
    }
  }
}

let sumcall = sum(2);
let bcall = sumcall(3);
let ccall = bcall(4)

console.log(ccall)


//clousers implements in curry
function sum(a){
  return function(b){
    return function(c){
      return a+b+c;
    }
  }
}

let sumcall = sum(2)(3)(4);
console.log(sumcall)


//2
var prism = l => w => h => l * w * h;
console.log(prism(2)(3)(5))


//infinite curring
function add(a){
  return function(b){
    if(b) return add(a+b);
    return a;
  }
}

console.log(add(2)(3)(4)(5)())
`.trim();

const generator = `
  function* generator_function() {
      yield 1;
      yield 2;
    }

   let generator = generator_function();

   console.log(generator.next().value);
   console.log(generator.return(22).value);
   console.log(generator.next().value);

   generator = generator_function();
   let iterable = generator[Symbol.iterator]();

   console.log(iterable.next().value);
   console.log(iterable.next().done);
 `.trim();

const yields = `
function* generator_function_1(){
  yield 2;
  yield 3;
}

function* generator_function_2() {
   yield 1;
   yield* generator_function_1();
   yield* [4, 5];
 }

var generator = generator_function_2();
console.log(generator.next().value);
console.log(generator.next().value);`.trim();

const stricts = `
'use strict';
delete Object.prototype;                                                                    // throws a TypeError.


//2
function sum(a, a, c) {                                                                     // syntax error.
  'use strict';
  return a + a + c;                                                                  // wrong if this code ran.
}
`.trim();

const management = `
var x = { a: { b: 2 } };

var y = x;                                                          
x = 1;                                      

var z = y.a;                                 
y = 'mozilla';                               
z = null;                                    
`.trim();

const eventLoop = `var eventLoop =[];
 var event;
 while(true){
    if (eventLoop.length > 0) {
      event = eventLoop.shift();
        try {
        event();
        }
      catch (err){
      reportError(err);
      }
    }
  }
 `.trim();

const security = `
//async
<script async src="js/script2.js"></script>
<script async src="js/script3.js"></script>


//defer
<script defer src="js/script2.js"></script>
<script defer src="js/script3.js"></script>
`.trim();

const proxies = `
let handler = {
  get: function(target, name) {
    return name in target? target[name] : 42
  }
}

let p = new Proxy({}, handler)
p.a = 1
console.log(p.a, p.b) `.trim();

const features = `import * as Module from './modules/module.js';`.trim();

const exportsing = `export { name, draw, reportArea, reportPerimeter };`.trim();

const loading = `
import('/index.js')
  .then((module) => {
    // Do something with the module.
  });
`.trim();

const Bubblings = `
<style>
div {
  min-width:100px;
  min-height: 100px;
  padding: 30px;
  border: 1px solid black;
}
</style>
</head>
<body>
<div id="grandParrent">
  <div id="parent">
    <div id="child">
    </div>
  </div>
</div>
<script src="input.js"></script>
</body> 


---------------------------------------------js-------------------

document.querySelector('#grandParrent')
  .addEventListener('click', ()=> {
