const shallow = `
//Shallow copy
const original = { a: 1, b: 2, c: { d: 3 } };
const shallowCopy = { ...original };

console.log(original);                              // { a: 1, b: 2, c: { d: 3 } }
console.log(shallowCopy);                           // { a: 1, b: 2, c: { d: 3 } }

original.c.d = 4;
console.log(original);                              // { a: 1, b: 2, c: { d: 4 } }
console.log(shallowCopy);                           // { a: 1, b: 2, c: { d: 4 } }


//Deep copy
const original = { a: 1, b: 2, c: { d: 3 } };
const deepCopy = JSON.parse(JSON.stringify(original));

console.log(original);                              // { a: 1, b: 2, c: { d: 3 } }
console.log(deepCopy);                              // { a: 1, b: 2, c: { d: 3 } }

original.c.d = 4;
console.log(original);                              // { a: 1, b: 2, c: { d: 4 } }
console.log(deepCopy);                              // { a: 1, b: 2, c: { d: 3 } }
`.trim();

