import React, { Component } from "react";
import Prism from "prismjs";
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import "../../ReactJs/styles.css";
import Sidebar from "../sidebar";
import PrismCode from "../../ReactJs/prismCode";

const titles = { backgroundColor: "#F0F8FF", padding: "1px", fontSize: "16px" };

const styles = (theme) => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  actionDiv: {
    textAlign: "center",
  },
});

const evalRPN = `
function policeNotation(tokens) {
  const stack = [];
  for (const tok of tokens) {
    if (!isNaN(tok)) {
      stack.push(parseInt(tok));
    } else {
      const b = stack.pop();
      const a = stack.pop();
      switch (tok) {
        case "+":
          stack.push(a + b);
          break;
        case "*":
          stack.push(a * b);
          break;
        case "-":
          stack.push(a - b);
          break;
        case "/":
          stack.push(a / b);
          break;
      }
    }
  }
  return stack.pop();
}

console.log(policeNotation(["2", "1", "+", "3", "*"]));
`.trim();

const convertToTitle = `
//Iterative
function fun(){
  const arr=["flower","flow","flight"];
  let result = arr[0];

  for(let i=0;i<arr.length;i++){
    while(arr[i].indexOf(result) !==0){
      result = result.substring(0, result.length-1)
    }
  }
  return console.log(result)
}

fun()


//Recursion
function fun(arr, index=0, result=arr[0]) {
  if (index === arr.length) {
    console.log(result);
    return;
  }

  while (arr[index].indexOf(result) !== 0) {
    result = result.substring(0, result.length - 1);
  }

  fun(arr, index + 1, result);
}

fun(["flower", "flow", "flight"]);


//Stack
function fun(arr) {
  if (arr.length === 0) {
    return "";
  }

  const stack = [...arr[0]];

  for (let i = 1; i < arr.length; i++) {
    while (arr[i].indexOf(stack.join("")) !== 0) {
      stack.pop();
    }
  }

  console.log(stack.join(""));
}

fun(["flower", "flow", "flight"]);
`.trim();

const combinationSum3 = `
function fun(){
  const arr=[-1,2,1,-4];
  let sum=0;

  for(let i=0;i<arr.length;i++){
    sum +=arr[i];
  }
  console.log(sum)
}

fun()


//Recursion
function fun(arr, index = 0) {
  if (arr.length==index) return 0; 
  return arr[index] + fun(arr, index + 1); 
}

console.log(fun([-1, 2, 1, -4]));


//Stack
function fun(arr) {
  let stack = 0;

  while (arr.length > 0) {
    stack += arr.pop();
  }

  console.log(stack);
}

fun([-1, 2, 1, -4]);


//Queue
function fun(arr) {
  let stack = 0;

  while (arr.length > 0) {
    stack += arr.shift();
  }

  console.log(stack);
}

fun([-1, 2, 1, -4]);
`.trim();

const closed3 = `
function fun() {
  const nums = [-1, 3, 2, 5, 10, 4, -2];
  const target = 1;
  let closestSum = 0;
  let closestTriplet = [];

  nums.sort((a,b)=>a-b)
  for (let i = 0; i < nums.length; i++) {
    for (let j = i + 1; j < nums.length; j++) {
      for (let k = j + 1; k < nums.length; k++) {
        const currentSum = nums[i] + nums[j] + nums[k];

        if (Math.abs(currentSum - target) < Math.abs(closestSum - target)) {
          closestSum = currentSum;
          closestTriplet = [nums[i], nums[j], nums[k]];
        }
      }
    }
  }

  console.log(closestTriplet);
}

fun();


// 2 pointer approach
function fun() {
  const nums = [-1, 3, 2, 5, 10, 4, -2];
  const target = 1;

  nums.sort((a, b) => a - b);
  let closestSum = 0;
  let closestTriplet = [];

  for (let i = 0; i < nums.length-2; i++) {
    let left = i + 1;
    let right = nums.length-1;

    while (left < right) {
      const currentSum = nums[i] + nums[left] + nums[right];

      if (Math.abs(currentSum - target) < Math.abs(closestSum - target)) {
        closestTriplet = [nums[i], nums[left], nums[right]];
      }

      if (currentSum < target) {
        left++;
      } else {
        right--;
      }
    }
  }

  console.log(closestTriplet);
}


fun();
`.trim();

const maxSlidingWindow = `
function fun() {
  const arr = {
    '2': ['a', 'b', 'c'],
    '3': ['d', 'e', 'f'],
    '4': ['g', 'h', 'i'],
    '5': ['j', 'k', 'l'],
    '6': ['m', 'n', 'o'],
    '7': ['p', 'q', 'r', 's'],
    '8': ['t', 'u', 'v'],
    '9': ['w', 'x', 'y', 'z'],
  };

  let digits = '23';
  let result = [];

  let newDigits = digits.split('');
  let one = newDigits[0];
  let two = newDigits[1];

  let arrOne = arr[one];
  let arrTwo = arr[two];

  if (arrOne && arrTwo) {
    for (let i = 0; i < arrOne.length; i++) {
      for (let j = 0; j < arrTwo.length; j++) {
        result.push(arrOne[i] + arrTwo[j]);
      }
    }
  }

  console.log(result);
}

fun();


//Hash table
function fun(digits) {
  const arr = {
    '2': ['a', 'b', 'c'],
    '3': ['d', 'e', 'f'],
