import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';


const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})

const operator = `
console.log("~", ~3,~2+ "<>", 1<2>3 ,"==!", 3==!2 ,"!==", 3!==2)      //!==

console.log([] == [])                                                 //false
`.trim();

const logged = `
var a = ['dog', 'cat', 'hen'];
a[100] = 'fox';
console.log(a.length);                                            //101
`.trim();

const addOp = `
console.log(0 && hi)                                              //0
`.trim();

const vals = `
const x = 6 % 2;
const y = x ? 'One' : 'Two';
console.log(y)                                                    //Two
`.trim();

const running = `
function logThis() {
  console.log(this);                                              //window
}
logThis();
`.trim();

const thiscode = `
const foo = [1, 2, 3];
const [n] = foo;
console.log(n);                                                 //1s
`.trim();

const doesCode = `
const arr1 = [2, 4, 6];
const arr2 = [3, 5, 7];

console.log([...arr1, ...arr2]);                               //[2, 4, 6, 3, 5, 7]
`.trim();

const snippet = `
var a = Math.max() < Math.min();
var b = Math.max() > Math.min();
console.log(a)                                                  //true
console.log(b)                                                  //false

console.log(NaN === NaN)                                        //false
console.log(typeof(NaN))                                        //number
console.log(parseInt("123Hello"))                               //123
console.log(parseInt("Hello123"))                               //NaN
`.trim();

const outputs = `
const fun = ({ a, b, c }) => {
  console.log(a, b, c);
 };
 fun(0, 1, 2);                                        //undefined undefined undefined


 //
x = 3;
console.log(x);
var x;


//since the initialization of "x" is not hoisted.
var x;
console.log(x);                                         //undefined
x = 23;
`.trim();

const inside = `
var x = 0;
var y = 23;

if(x) { console.log(x) }   // The code inside this block will not run since the value of x is 0(Falsy)  
if(y) { console.log(y) }    // The code inside this block will run since the value of y is 23 (Truthy)
`.trim();

const thisKey = ``.trim();



class McqJs extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0)
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4><Sidebar /></h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>1. Which operator returns true if the two compared values are not equal?</h3>
              <div style={titles}>
                <PrismCode
                  code={operator}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>2. How is a forEach statement different from a for statement?</h3>
              A for statement is generic, but a forEach statement can be used only with an array.
              <br/>

              <h3>3. How does a function create a closure?</h3>
              It returns a reference to a variable in its parent scope.
              <br/>

              <h3>4. Which Object method returns an iterable that can be used to iterate over the properties of an object?</h3>
              Object.keys()
              <br/>

              <h3>5. What will be logged to the console?</h3>
              <div style={titles}>
                <PrismCode
                  code={logged}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>6. Add operator.</h3>
              <div style={titles}>
                <PrismCode
                  code={addOp}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>7. Which variable is an implicit parameter for every function in JavaScript?</h3>
              Arguments
              <br/>

              <h3>8. What will the value of y be in this code:</h3>
              <div style={titles}>
                <PrismCode
                  code={vals}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>9. Which keyword is used to create an error?</h3>
              throw
              <br/>

              <h3>10. What is the result in the console of running this code?</h3>
              <div style={titles}>
                <PrismCode
                  code={running}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>11. What will this code log to the console?</h3>
              <div style={titles}>
                <PrismCode
                  code={thiscode}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>12. What does this code do?</h3>
              <div style={titles}>
                <PrismCode
                  code={doesCode}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>13. Upon encountering empty statements, what does the Javascript Interpreter do?</h3>
                Ignores the statements.
              <br/>

              <h3>14. What will be the output of the following code snippet?</h3>
              <div style={titles}>
                <PrismCode
                  code={snippet}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>15. </h3>
              <div style={titles}>
                <PrismCode
                  code={outputs}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>16.</h3>
              <div style={titles}>
                <PrismCode
                  code={inside}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3></h3>

              <div style={titles}>
                <PrismCode
                  code={thisKey}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3></h3>

              <div style={titles}>
                <PrismCode
                  code={thisKey}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3></h3>

              <div style={titles}>
                <PrismCode
                  code={thisKey}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3></h3>

              <div style={titles}>
                <PrismCode
                  code={thisKey}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
            </List>
          </Paper>
        </Grid>
      </Grid>
    )
  }
}

export default (withStyles(styles)(McqJs));
