import React, { Component } from "react";
import Prism from "prismjs";
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import "../ReactJs/styles.css";
import Sidebar from "./sidebar";
import PrismCode from "../ReactJs/prismCode";

const titles = { backgroundColor: "#F0F8FF", padding: "1px", fontSize: "16px" };

const styles = (theme) => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  actionDiv: {
    textAlign: "center",
  },
});

const flextFloat = `
.container {
  display: flex;
  justify-content: space-between;
}

.img-box {
  height: 200px;
  width: 150px;
}


<div class="container">
  <img src="imgs.jpg" alt="" class="img-box" />
  <img src="imgs.jpg" alt="" class="img-box" />
  <img src="imgs.jpg" alt="" class="img-box" />
</div>
`.trim();

const columns = `
.row {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
}

.column {
  flex-basis: calc(100% / 3 - 20px);
  margin: 10px;
}

img {
  width: 100%;
  height: auto;
}


<div class="row">
  <div class="column">
    <img src="imgs.jpg" alt="" />
  </div>
  <div class="column">
    <img src="imgs.jpg" alt="" />
  </div>
  <div class="column">
    <img src="imgs.jpg" alt="" />
  </div>
</div>
`.trim();

const gridimgs = `
.grid-container {
  display: grid;
  grid-template-columns: auto auto auto;
}

.img {
  height: 100px;
  width: 100px;
}

<div class="grid-container">
  <img src="imgs.jpg" alt="" class="img" />
  <img src="imgs.jpg" alt="" class="img" />
  <img src="imgs.jpg" alt="" class="img" />
</div>
`.trim();

const textCenter = `
.circle {
  width: 100px;
  height: 100px;
  background-color: #999;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
}

<div class="circle">Text Here</div>
`.trim();

const rowColom = `
.circle {
  display: flex;
  justify-content: center;
  flex-direction: column;
}

<div class="circle">
  <p>Text Here</p>
  <p>Text Here</p>
  <p>Text Here</p>
  <p>Text Here</p>
  <p>Text Here</p>
</div>
`.trim();

const btnRit = `
.btn {
  float: right;
}

<button class="btn">Click me!</button>
`.trim();

const boxCircle = `
<style>
  .container {
    width: 400px;
    height: 400px;
    background-color: green;
    border-radius: 50%;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .box {
    height: 100px;
    width: 100px;
    background-color: red;
  }
</style>

<div class="container">
  <p class="box"></p>
</div>
`.trim();

const cusGrid = `
<style>
  .grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-gap: 20px;
  }

  .col {
    background-color: #f2f2f2;
    padding: 20px;
  }
</style>

<div class="grid">
  <div class="col">Column 1</div>
  <div class="col">Column 2</div>
  <div class="col">Column 3</div>
</div>
`.trim();

// const flextFloat = ``.trim();

// const flextFloat = ``.trim();

// const flextFloat = ``.trim();

// const flextFloat = ``.trim();

// const flextFloat = ``.trim();

// const flextFloat = ``.trim();

// const flextFloat = ``.trim();

// const flextFloat = ``.trim();

// const flextFloat = ``.trim();

// const flextFloat = ``.trim();

// const flextFloat = ``.trim();

class CssCodes extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0);
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4>
              <Sidebar />
            </h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>Three imgege in row(flex)</h3>
              <div style={titles}>
                <PrismCode
                  code={flextFloat}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <h3>Three imgege in row(column)</h3>
              <div style={titles}>
                <PrismCode
                  code={columns}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <h3>Three imgege in row(Grid)</h3>

              <div style={titles}>
                <PrismCode
                  code={gridimgs}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <h3>Text in center</h3>
              <div style={titles}>
                <PrismCode
                  code={textCenter}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <h3>Text row to column</h3>
              <div style={titles}>
                <PrismCode
                  code={rowColom}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <h3>Button right</h3>
              <div style={titles}>
                <PrismCode
                  code={btnRit}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <h3>Box in circle</h3>

              <div style={titles}>
                <PrismCode
                  code={boxCircle}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <h3>Customizable Grid</h3>

              <div style={titles}>
                <PrismCode
                  code={cusGrid}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <h3>Fade</h3>

              <div style={titles}>
                <PrismCode
                  code={flextFloat}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              {/* <h3></h3>

              <div style={titles}>
                <PrismCode
                  code={flextFloat}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br /> */}

              <h3></h3>

              {/* <div style={titles}>
                <PrismCode
                  code={flextFloat}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div> */}
            </List>
          </Paper>
        </Grid>
      </Grid>
    );
  }
}

export default withStyles(styles)(CssCodes);
