import React, { Component } from "react";
import Prism from "prismjs";
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import "../../ReactJs/styles.css";
import Sidebar from "../sidebar";
import PrismCode from "../../ReactJs/prismCode";

const titles = { backgroundColor: "#F0F8FF", padding: "1px", fontSize: "16px" };

const styles = (theme) => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  actionDiv: {
    textAlign: "center",
  },
});

const subscribes = `
obs.subscribe({
  next:(data)=>console.log('obj',data),
  error:(err)=>console.log('obj',err),
  complete:()=>console.log('complate'),
})`.trim();

const rxjsfrom = `
//import { from } from 'rxjs';
export class AppComponent  {
  ngOnInit(){
    const data = from(fetch('https://jsonplaceholder.typicode.com/posts'));

    data.subscribe({
      next(res) { console.log(res); },
      error(err) { console.error('Error: ' + err); },
      complete() { console.log('Completed'); }
    });
  }
}
`.trim();

const mapfil = `
//import { of } from 'rxjs';
//import { map } from 'rxjs/operators';

export class AppComponent  {
  val:any=[];

  ngOnInit(){
    const nums = of(1, 2, 3);

    const squr = map((val: number) => val * val);
    const sqNum = squr(nums);

    sqNum.subscribe(x => this.val.push(x));
  }
}
`.trim();

const mapipe = `
//import { of } from 'rxjs';
//import { filter, map } from 'rxjs/operators';

export class AppComponent  {
  val:any=[];

  ngOnInit(){
    const squareOdd = of(1, 2, 3, 4, 5)
    .pipe(
      filter(n => n % 2 !== 0),
      map(n => n * n)
    );

    squareOdd.subscribe(x => this.val.push(x));
  }
}
`.trim();

const stcreate = `
//import { Component, ElementRef, ViewChild } from '@angular/core';
//import { fromEvent } from 'rxjs';

export class AppComponent  {
  constructor(){}
  @ViewChild('addBtn')
  addBtn!: ElementRef;

  //stream create
  ngAfterViewInit(){
    fromEvent(this.addBtn.nativeElement, 'click').subscribe(res=>{
      console.log(res)
    })
  }
}
`.trim();

const unsubscribe = `
//import { interval, Subscription } from 'rxjs';
export class AppComponent  {
  intervalval=0;
  videoSub!: Subscription;

  ngOnInit(){
    const broadcast = interval(1000)
    this.videoSub = broadcast.subscribe(res=>{
      this.intervalval = res;

      if(res>5){
        this.videoSub.unsubscribe()
      }
    })
  }
}
`.trim();

const throttleTime = `
var observable = Rx.Observable.interval(1000);
var observer = {
  next: function (value) {
    console.log(value);
  }
};

observable.map(function (value) {
  return 'Number: ' + value;
}).throttleTime(1900).subscribe(observer);
`.trim();

const fixrxjs = `
var subject = new Rx.Subject();

subject.subscribe({
  next: function (value) {
    console.log(value);
  },
  error: function (error) {
    console.log(error);
  },
  complete: function () {
    console.log('Complete');
  }
});

subject.subscribe({
  next: function (value) {
    console.log(value);
  }
});

subject.next('A new data piece');
subject.complete();
subject.next('New value');
`.trim();

const filter = `
import {interval} from 'rxjs';
import {filter, map} from "rxjs/operators";

const observable = interval(100).pipe(filter(data=>{
  return data<5
}));

observable.subscribe(data => {
  console.log(data);
})
`.trim();

const rxjxLibrary = `import { Observable, throwError } from 'rxjs';
import { catchError, retry } from 'rxjs/operators';`.trim();

const observableFun = `import { from } from 'rxjs'; // from function
const data = from(fetch('/api/endpoint')); //Created from Promise
data.subscribe({
 next(response) {console.log(response);},
 error(err) {console.error('Error: ' + err);},
 complete() {console.log('Completed');}
});`.trim();

const observableAJAX = `
import { ajax } from 'rxjs/ajax'; // ajax function
const apiData = ajax('/api/data'); // Created from AJAX request
// Subscribe to create the request
apiData.subscribe(res => console.log(res.status, res.response));`.trim();

const observableCount = `
import { interval } from 'rxjs'; // interval function
const secondsCounter = interval(1000); // Created from Counter value
secondsCounter.subscribe(n =>
  console.log('Counter value: '$'{n}'));`.trim();

const observableEvt = `
import { fromEvent } from 'rxjs';
const el = document.getElementById('custom-element');
const mouseMoves = fromEvent(el, 'mousemove');
const subscription = mouseMoves.subscribe((e: MouseEvent) => {
  console.log('Coordnitaes of mouse pointer: '$'{e.clientX} * '$'{e.clientY}');
  });`.trim();

const placeholders = `
//app.component.ts
import {PlaceholderService} from "./services/placeholder.service"

@Component({
  selector: 'app-root',
  template: '
  <button (click)="getApi()">GET</button>
    <div *ngFor="let items of data">
      <p>{{items.id}} - {{items.title}}</p>
    </div>
  ',
})

export class AppComponent implements OnInit{
  data:Array<any>;

  constructor( private https:PlaceholderService) { 
    this.data=new Array<any>()
   }

  ngOnInit(): void {}
  getApi(){
    this.https.getData().subscribe((data)=>{
      this.data=data
    })
  }
}


//services/placeholder.service.ts
import { Injectable } from '@angular/core';
import {HttpClient} from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class PlaceholderService {

  constructor(private http:HttpClient) { }

  getData(): Observable<any>{
    const url="https://jsonplaceholder.typicode.com/posts";
    return this.http.get<any>(url)
  }
}
`.trim();

const notifications = `
interface Observer<T> {
  closed?: boolean;
  next: (value: T) => void;
  error: (err: any) => void;
  complete: () => void;
}
`.trim();

const otsbservable = `
import { Observable } from "rxjs/Observable";
// import "rxjs/add/operator/pluck";

const obs = new Observable((observer)=>{
  let counter=1;
  const producer = setInterval(()=>{
    observer.next(counter++)
  },1000)
})

const sub = obs.subscribe({
  next:(data)=>console.log('obj1',data),
  error:(err)=>console.log('obj1',err),
  complete:()=>console.log('complate'),
})

setTimeout(()=>{
  sub.unsubscribe();
},5000)`.trim();

const otsbservable2 = `
import { Observable } from "rxjs/Observable";

const obs = new Observable((observer) => {
  let counter = 1;
  const producer = setInterval(() => {
    observer.next(counter++);

    if (counter > 5) {
      observer.error("oops");
      observer.complete();
    }
  }, 1000);

  return () => {
    console.log("unsubscribe done");
    clearInterval(producer);
  };
});

const sub = obs.subscribe({
  next: (data) => console.log("obj1", data),
  error: (err) => console.log("obj1", err),
  complete: () => console.log("complate"),
});`.trim();

const otsbservable3 = `
import { Observable } from "rxjs/Observable";

const obs = new Observable((observer)=>{
  let counter=1;
  const producer = setInterval(()=>{
    observer.next(counter++)

    if(counter>5){
      observer.error('oops')
    }
  },1000);

  return()=>{
    console.log('unsubscribe done')
    clearInterval(producer)
  }
})

const sub = obs.subscribe({
  next:(data)=>console.log('obj1',data),
  error:(err)=>console.log('obj1',err),
  complete:()=>console.log('complate'),
})

setTimeout(()=>{
  const sub2 = obs.subscribe({
    next:(data)=>console.log('obj2',data),
    error:(err)=>console.log('obj2',err),
    complete:()=>console.log('complate2'),
  })
},3000)

setTimeout(()=>{
  sub.unsubscribe();
},5000);`.trim();

const otsbservable4 = `
import { Observable } from 'rxjs';

@Component({
  selector: 'app-root',
  template: '<p>{{ data }}</p>',
})
export class AppComponent {
  data: any = '';

  ngOnInit() {
    const myObservable = new Observable((observer) => {
      setTimeout(() => {
        observer.next('Hello');
      }, 1000);

      setTimeout(() => {
        observer.next('World!');
        observer.complete();
      }, 2000);
    });
    myObservable
      .toPromise()
      .then((datas) => {
        this.data = datas;
      })
      .catch((error) => {
        console.error(error);
      });
  }
}
`.trim();

const otsbservable5 = `
import { of } from 'rxjs';

@Component({
  selector: 'app-root',
  template: '{{ values | async }}',
})
export class AppComponent {
  values = of(1, 2, 3, 4, 5);
}
`.trim();

