const otsbservable8 = `
import { interval, from } from "rxjs";

const obs$ = interval(1000);
const sub$ = obs$.subscribe((i) => {
  if (i > 10) {
    sub$.unsubscribe();
    return;
  }
  console.log(i);
});

//2
const sub2$ = obs$.subscribe((i) => {
  if (i > 2) {
    sub2$.unsubscribe();
    return;
  }
  from(
    fetch("https://jsonplaceholder.typicode.com/posts/" + (i + 1)).then((res) =>
      res.json()
    )
  ).subscribe(console.log);
});
`.trim();

const otsbservable9 = `
import { timer, from } from "rxjs";

