const fruit = new Fruit({
  name: "Apple",
  rating: 7,
  review: "Pretty solid as a fruit."
});

//fruit.save();

const personSchema = new mongoose.Schema({
  name: String,
  age: Number,
  favoriteFruit: fruitSchema
});

const Person = mongoose.model("Person", personSchema);

//personJohn.save();

const banana = new Fruit({
  name: "Banana",
  rating: 6,
  review: "Weird texture."
});

const person = new Person({
  name: "Kevin",
  age: 25,
  favoriteFruit: banana
});

const orange = new Fruit({
  name: "Orange",
  rating: 8,
  review: "Tastes great."
});

// person.save();
Person.updateOne({ name: "John" }, { favoriteFruit: orange }, function (err) {
  if (err) {
    console.log(err);
  } else {
    console.log("Success");
  }
});

const kiwi = new Fruit({
  name: "Kiwi",
  rating: 4,
  review: "Too sweet for me."
});

Fruit.insertMany([banana, kiwi, orange], function (err) {
  if (err) {
    console.log(err);
  } else {
    console.log("Successfully saved all the fruits");
  }
});

Fruit.find(function (err, fruits) {
  if (err) {
    console.log(err);
  } else {
    mongoose.connection.close();

    fruits.forEach(function (fruit) {
      console.log(fruit.name);
    });
  }
});
`.trim();


class Ejs extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0)
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4><Sidebar /></h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>1. ToDo List</h3>
              <div style={titles}>
                <PrismCode
                  code={todoList}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />
              <b>ejs</b>
              <div style={titles}>
                <PrismCode
                  code={todoListEjs}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>2. Blog-website</h3>
              <div style={titles}>
                <PrismCode
                  code={blogWebsite}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <b>views/</b>
              <div style={titles}>
                <PrismCode
                  code={viewEjs}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <b>views/partials</b>
              <div style={titles}>
                <PrismCode
                  code={partials}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>3. Fruits</h3>
              <div style={titles}>
                <PrismCode
                  code={fruitsProject}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
            </List>
          </Paper>
        </Grid>
      </Grid>
    )
  }
}

export default (withStyles(styles)(Ejs));
