import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';

import Stcksval from '../../../assets/stcks.png';

const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const redesign = {
  height: 200,
  width: 500
}

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})


const largestPerimeter = `
const largestPerimeter = function (A = []) {
  A.sort((a, b) => b - a)
  for (let i = 0; i < A.length - 2; i++) {
    if (A[i] < A[i + 1] + A[i + 2]) {
      return A[i] + A[i + 1] + A[i + 2]
    }
  }
  return 0
}

console.log(largestPerimeter([2,1,2]))`.trim();

const brokenCalc = `
const brokenCalc = function (X, Y) {
  let result = 0
  while (Y > X) {
    if (Y % 2 === 0) {
      Y /= 2
    } else {
      Y += 1
    }
    result += 1
  }
  return result + X - Y
}

console.log(brokenCalc(5, 8))`.trim();

const shuffle = `
function shuffle(nums, n) {
  const result = []
  for (let i = 0; i < n; i++) {
    result.push(nums[i], nums[i + n])
  }
  return result
};

console.log(shuffle([2,5,1,3,4,7], 3))`.trim();

const destCity = `
const destCity = function (paths) {
  let result = ''
  const map = paths.reduce((acc, [a, b]) => {
    acc[a] = 1
    acc[b] = (acc[b] || 0)
    if (acc[b] === 0) {
      result = b
    }
    return acc
  }, {})
  return Object.keys(map).filter(x => map[x] === 0)[0]
}

console.log(destCity([["London","New York"],["New York","Lima"],["Lima","Sao Paulo"]]))`.trim();

const maxScore = `
const maxScore = function (nums, k) {
  const size = nums.length - k
  let currentSum = nums.slice(0, size).reduce((acc, num) => acc + num, 0)
  let min = currentSum
  for (let i = size; i < nums.length; i++) {
    currentSum = currentSum + nums[i] - nums[i - size]
    min = Math.min(min, currentSum)
  }
  return nums.reduce((acc, num) => acc + num, 0) - min
}

console.log(maxScore([1,79,80,1,1,1,200,1], 3))`.trim();

const jump = `
const jump = (nums = []) => {
  let steps = 0
  let currentJumpIndex = 0
  let nextJumpIndex = 0
  for (let i = 0; i < nums.length - 1; i++) {
    nextJumpIndex = Math.max(i + nums[i], nextJumpIndex)
    if (i === currentJumpIndex) {
      steps += 1
      currentJumpIndex = nextJumpIndex
    }
  }
  return steps
}

console.log(jump([2,3,1,1,4]))`.trim();





const numTimesAllBlue = `
const numTimesAllBlue = function (light) {
  let moment = 0
  let max = -1
  for (let i = 0; i < light.length; i++) {
    max = Math.max(max, light[i])
    if (i + 1 === max) {
      moment += 1
    }
  }
  return moment
}

console.log(numTimesAllBlue([3,2,4,1,5]))`.trim();


class DSLogic7 extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0)
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4><Sidebar /></h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>1. Largest Perimeter Triangle.</h3>
              Given an array A of positive lengths, return the largest perimeter of a triangle with non-zero area, formed from 3 of these lengths.
              <br/>
              If it is impossible to form any triangle of non-zero area, return 0.
              <div style={titles}>
                <PrismCode
                  code={largestPerimeter}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>2. Broken Calculator.</h3>
              On a broken calculator that has a number showing on its display, we can perform two operations:
              <br/>
              <ul>
                <li><b>Double: </b>Multiply the number on the display by 2, or;</li>
                <li><b>Decrement: </b>Subtract 1 from the number on the display.</li>
              </ul>
              Initially, the calculator is displaying the number X.
              <br/>
              Return the minimum number of operations needed to display the number Y.
              <div style={titles}>
                <PrismCode
                  code={brokenCalc}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>4. Shuffle the Array.</h3>
              <b>Input: </b>nums = [2,5,1,3,4,7], n = 3<br/>
              <b>Output: </b>[2,3,5,4,1,7] <br/>
              <b>Explanation: </b>Since x1=2, x2=5, x3=1, y1=3, y2=4, y3=7 then the answer is [2,3,5,4,1,7].
              <div style={titles}>
                <PrismCode
                  code={shuffle}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>5. Destination City.</h3>
              You are given the array paths, where paths[i] = [cityAi, cityBi] means there exists a direct path going from cityAi to cityBi. Return the destination city, that is, the city without any path outgoing to another city.
              <br/>
              It is guaranteed that the graph of paths forms a line without any loop, therefore, there will be exactly one destination city.
              <br/>
              <b>Input: </b>paths = [["London","New York"],["New York","Lima"],["Lima","Sao Paulo"]]
              <b>Output: </b>"Sao Paulo" 
              <b>Explanation: </b>Starting at "London" city you will reach "Sao Paulo" city which is the destination city. Your trip consist of: "London" - "New York" - "Lima" - "Sao Paulo".

              <div style={titles}>
                <PrismCode
                  code={destCity}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>6. Maximum Points You Can Obtain from Cards.</h3>
              There are several cards arranged in a row, and each card has an associated number of points The points are given in the integer array cardPoints.
              <br/>
              In one step, you can take one card from the beginning or from the end of the row. You have to take exactly k cards.
              <br/>
              Your score is the sum of the points of the cards you have taken.
              <br/>
              Given the integer array cardPoints and the integer k, return the maximum score you can obtain.
              <div style={titles}>
                <PrismCode
                  code={maxScore}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>7. Jump Game.</h3>
              Given an array of non-negative integers, you are initially positioned at the first index of the array.
              <br/>
              Each element in the array represents your maximum jump length at that position.
              <br/>
              Your goal is to reach the last index in the minimum number of jumps.
              <br/>
              <br/>
              <b>Input: </b>[2,3,1,1,4]<br/>
              <b>Output: </b>2<br/>
              <b>Explanation: </b>The minimum number of jumps to reach the last index is 2.
                  Jump 1 step from index 0 to 1, then 3 steps to the last index.
              <div style={titles}>
                <PrismCode
                  code={jump}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>






            </List>
          </Paper>
        </Grid>
      </Grid>
    )
  }
}

export default (withStyles(styles)(DSLogic7));
