import React, { Component } from "react";
import Prism from "prismjs";
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import "../../ReactJs/styles.css";
import Sidebar from "../sidebar";
import PrismCode from "../../ReactJs/prismCode";
import Graphs from "../../../assets/dinjection.png";

const titles = { backgroundColor: "#F0F8FF", padding: "1px", fontSize: "16px" };

const styles = (theme) => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  actionDiv: {
    textAlign: "center",
  },
});

const redesign = {
  height: 200,
  width: 500,
};

const NgClass = `
@Component({
  selector: 'app-root',
  template: '
  <p [ngStyle] = "{backgroundColor: getColor()}" [ngClass] = "{Online: serverStatus === 'Online'}"> 
    Server  with ID {{serverID}} is {{serverStatus}}. 
  </p >,
  styles: ['.Online{ color: yellow; }']'})

export class AppComponent {
  serverID: number = 10;
  serverStatus: string = 'Offline';

  constructor () {
  this.serverStatus = Math.random() > 0.5 ? 'Online' : 'Offline';
  }

  getServerStatus() {
    return this.serverStatus;
  }
  getColor() {
    return this.serverStatus === 'Online' ? 'green' : 'red';
  }
}`.trim();

const databinding = `
DOM               Component
<----{{value}}-----
<-[propery]="value" --
--(event)="handler" -->
<-[(NgModule)]="propery" --`.trim();

const property = `
syntax: [property]='expression

//
@Component({
  selector: 'app-root',
  template: '
    <button (click)="toggleSecret()">Toggle Secret</button>
    <h3 [hidden]="secretTextHidden">Property Binding</h3>
  ',
})
export class AppComponent {
  secretTextHidden = false;

  toggleSecret() {
    this.secretTextHidden = !this.secretTextHidden;
  }
}
`.trim();

const evtBinding = `
@Component({
  selector: 'app-root',
  template: '
    {{ name }}
    <button (click)="nameChange()">evt</button>
  ',
})
export class AppComponent {
  name = 'Pikachu';

  nameChange() {
    this.name = 'Raichu';
  }
}`.trim();

const NgStyles = `
//1
<div [ngStyle]="{'color':green}"></div>
ngStyle become much more useful when the value is dynamic.
<div [ngStyle]="{'color': person.country==='UK' ? 'green' : 'red'}"></div>


//2
@Component({
  selector: 'app-root',
  template: '
  <p[ngStyle]="{backgroundColor: 'green'}"> Server with ID {{serverID}} is {{serverStatus}}.</p>
  '})
export class AppComponent {
  serverID: number = 10;
  serverStatus: string = 'Offline';

  constructor () {
  this.serverStatus = Math.random() > 0.5 ? 'Online' : 'Offline';
  }
}`.trim();

const customDir = `
//html
<p appCustomdir>Custom directive</p>


//ts
import { Directive, ElementRef } from '@angular/core';

@Directive({
  selector: '[appCustomdir]'
})
export class CustomdirDirective {

  constructor(private el:ElementRef) { 
    el.nativeElement.style.color="green"
  }
}
`.trim();
const templateExpree = `
<h3>{{username}}, welcome to Angular</h3>


//The below javascript expressions are prohibited in template expression
1. assignments (=, +=, -=, ...)
2. new
3. chaining expressions with ; or ,
4. increment and decrement operators (++ and --)
`.trim();

const templateState = `
//template statements appear in quotes to the right of the = symbol like (event)="statement".
<button (click)="statement()">Edit Profile</button>
`.trim();

const twoWay = `
@Component({
  selector: 'app-root',
  template: '
    <input type="text" [(ngModel)]="name" placeholder="type here" />
    <h3>{{ name }}</h3>
  ',
})
export class AppComponent {
  name = 'Angular 5';
}
`.trim();

const hostBinding = `
import { Component, ElementRef, HostBinding, HostListener} from '@angular/core';

@Component({
  selector: 'app-root',
  template: '
      <p>@HostBinding() & @HostListener()</p>
  ',
  providers:[]
})

export class AppComponent{
  constructor(private el:ElementRef){

  }

  @HostBinding('style.color') textColor: any;
  ngOnInit(){
    this.textColor="green";
  }

  @HostListener('click') onClick(){
    // alert('HostListener')
    this.textColor = "red";
  }

  @HostListener('mouseover') onMouseHover(){
    this.textColor = "blue";
  }

  @HostListener('mouseout') onMouseOut(){
    this.textColor = "pink";
  }
}`.trim();

const parameters = `
//myservice.ts
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class myService {
  name = 'Mukesh';
  constructor() {}
}


//app.component.ts
import { Component, Inject } from '@angular/core';
import { myService } from './myservice';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  constructor(@Inject(myService) myService: any) {
    console.log(myService);
  }
}
`.trim();

const makeServices = `
//myService.ts
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class myService {
  name = 'Mukesh';
  sayHello() {
    console.log(this.name);
  }
}


//app.component.ts
import { myService } from './myservice';

@Component({
  selector: 'app-root',
  template: '',
})
export class AppComponent {
  constructor(private myServices: myService) {
    this.myServices.sayHello();
  }
}


//app.module.ts
@NgModule({
  declarations: [AppComponent, Child],
  imports: [
    BrowserModule,
    AppRoutingModule,
    FormsModule,
    StoreModule.forRoot({}),
  ],
  providers: [myService],
  bootstrap: [AppComponent],
})
`.trim();

const makeServices1 = `
@Injectable({
  providedIn: 'root'
})`.trim();

const makeServices2 = `
import { Injectable } from '@angular/core';
import { MyModule } from './my.module';

@Injectable({
  providedIn: MyModule
})
export class MyService {}`.trim();

const postals = `
  class Number{
    constructor(){}
  }

  class Address{
    constructor(){}
  }

  class PostalDetails{
    Number;
    Address;

    constructor(){
      this.Number = new Number();
      this.Address = new Address();
    }
  }
`.trim();

const postalsparams = `
class Number{
  constructor(par){}
}

class Address{
  constructor(par){}
}

class PostalDetails{
  Number;
  Address;

  constructor(){
    this.Number = new Number();     //Error
    this.Address = new Address();   //Error
  }
}
`.trim();

const impdi = `
class PostalDetails{
  number;
  address;

  constructor(number, address){
    this.number = number();    
    this.address = address();  
  }
}
`.trim();

const httpClients = `
import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Kafein {
  name: string;
  address: string;
}

@Component({
  selector: 'app-root',
  template: ' <tr *ngFor="let datas of httpData">
    <td>{{ datas.title }}</td>
  </tr>',
})
export class AppComponent {
  url = 'https://jsonplaceholder.typicode.com/todos';
  httpData: any;

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.callApi();
  }

  callApi() {
    this.http.get<Kafein[]>(this.url).subscribe((data) => {
      this.httpData = data;
    });
  }
}
`.trim();

const reactiveforms = `
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-root',
  template: '
    <form [formGroup]="form" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <input type="text" [ngClass]="{ 'is-invalid': submitted }" />
        <div *ngIf="submitted">Enter text</div>
      </div>

      <button type="submit">Register</button>
    </form>
  '',
})
export class AppComponent implements OnInit {
  form: any = FormGroup;
  submitted = false;

  constructor(private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    this.form = this.formBuilder.group({
      name: ['', Validators.required],
    });
  }

  onSubmit(): void {
    this.submitted = true;

    if (this.form.invalid) {
      return;
    }
  }
}
`.trim();

const formbuildes = `
//FormGroup
import { FormGroup, FormControl } from '@angular/forms';

@Component({
  selector: 'app-root',
  template: '
    <div [formGroup]="frmGroup">
      <input type="text" formControlName="firstName" /><br />
      <input type="text" formControlName="lastName" />
    </div>
    <p>Is FormGroup Valid: {{ frmGroup.valid }}</p>
  ',
})
export class AppComponent {
  name = 'Angular';
  frmGroup = new FormGroup({
    firstName: new FormControl('', []),
    lastName: new FormControl('', []),
  });

  constructor() {
    this.frmGroup.valueChanges.subscribe((val) => {
      console.log(val.firstName);
    });
  }
}


//FormBuilder
import { FormGroup, FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-root',
  template: '
    <form (ngSubmit)="save()" [formGroup]="myGroup">
      FIRST NAME <input formControlName="firstName" /> <br />
      MIDDLE NAME <input formControlName="middleName" /> <br />
      LAST NAME<input formControlName="lastName" />
    </form>
  ',
})
export class AppComponent {
  myGroup: any = FormGroup;
  constructor(private fb: FormBuilder) {}

  ngOnInit() {
    this.myGroup = this.fb.group({
      firstName: 'Chethana',
      middleName: [{ value: 'Dibbahalli', disabled: true }],
      lastName: { value: 'DC', disabled: true },
    });
  }

  save() {
    console.log('Inside save' + this.myGroup.value);
  }
}

`.trim();

class IntroAngulard extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0);
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4>
              <Sidebar />
            </h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>1. Please explain the various features of Angular.</h3>
              <ul>
                <li>
                  <b>Components: </b>Angular is a component-based framework,
                  which means that applications are built by assembling
                  components that represent different parts of the application's
                  user interface. Components are reusable, self-contained, and
                  can be easily combined to create complex user interfaces.
                </li>
                <br />
                <li>
                  <b>Templates: </b> Angular uses templates to define the user
                  interface of a component. Templates are written in HTML and
                  contain Angular-specific syntax that allows for data binding
                  and other features.
                </li>
                <br />
                <li>
                  <b>Directives: </b>Angular provides a number of built-in
                  directives that allow you to manipulate the DOM, add or remove
                  elements, and apply conditional logic to your templates. You
                  can also create custom directives to extend the functionality
                  of Angular.
                </li>
                <br />
                <li>
                  <b>Services: </b>Services are used to provide functionality
                  that can be shared across different components of an
                  application. Services can be used for things like fetching
                  data from a server, performing complex calculations, or
                  managing state.
                </li>
                <br />
                <li>
                  <b>Dependency Injection: </b>Angular's dependency injection
                  system allows you to easily manage dependencies between
                  different components and services. Dependencies are injected
                  into components and services through their constructors.
                </li>
                <br />
                <li>
                  <b>Routing: </b>Angular's routing system allows you to define
                  routes for different parts of your application and navigate
                  between them using URLs.
                </li>
                <br />
                <li>
                  <b>Forms: </b>Angular provides powerful support for building
                  forms, including both template-driven and reactive forms. This
                  includes features like form validation, form submission, and
                  handling user input.
                </li>
                <br />
                <li>
                  <b>Observables: </b>Angular uses observables to handle
                  asynchronous operations such as HTTP requests. Observables
                  provide a way to work with asynchronous data streams in a more
                  efficient and predictable way.
                </li>
                <br />
                <li>
                  <b>Testing: </b>Angular provides a number of tools and
                  utilities for testing your application, including both unit
                  tests and end-to-end tests. This includes features like the
                  Angular testing library and the Protractor testing framework.
                </li>
                <br />
                <li>
                  <b>Mobile Support: </b>Angular provides support for building
                  mobile applications using technologies like Ionic and
                  NativeScript.
