import React, { Component } from "react";
import Prism from "prismjs";
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import "../../ReactJs/styles.css";
import Sidebar from "../sidebar";
import PrismCode from "../../ReactJs/prismCode";
import Graphs from "../../../assets/dinjection.png";
import formroot from "../../../assets/HhQY9.png";

const titles = { backgroundColor: "#F0F8FF", padding: "1px", fontSize: "16px" };

const styles = (theme) => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  actionDiv: {
    textAlign: "center",
  },
});

const redesign = {
  height: 200,
  width: 500,
};

const NgClass = `
@Component({
  selector: 'app-root',
  template: '
  <p [ngStyle] = "{backgroundColor: getColor()}" [ngClass] = "{Online: serverStatus === 'Online'}"> 
    Server  with ID {{serverID}} is {{serverStatus}}. 
  </p >,
  styles: ['.Online{ color: yellow; }']'})

export class AppComponent {
  serverID: number = 10;
  serverStatus: string = 'Offline';

  constructor () {
  this.serverStatus = Math.random() > 0.5 ? 'Online' : 'Offline';
  }

  getServerStatus() {
    return this.serverStatus;
  }
  getColor() {
    return this.serverStatus === 'Online' ? 'green' : 'red';
  }
}`.trim();

const databinding = `
DOM               Component
<----{{value}}-----
<-[propery]="value" --
--(event)="handler" -->
<-[(NgModule)]="propery" --`.trim();

const property = `
syntax: [property]='expression

//
@Component({
  selector: 'app-root',
  template: '
    <button (click)="toggleSecret()">Toggle Secret</button>
    <h3 [hidden]="secretTextHidden">Property Binding</h3>
  ',
})
export class AppComponent {
  secretTextHidden = false;

  toggleSecret() {
    this.secretTextHidden = !this.secretTextHidden;
  }
}
`.trim();

const evtBinding = `
@Component({
  selector: 'app-root',
  template: '
    {{ name }}
    <button (click)="nameChange()">evt</button>
  ',
})
export class AppComponent {
  name = 'Pikachu';

  nameChange() {
    this.name = 'Raichu';
  }
}`.trim();

const NgStyles = `
//1
<div [ngStyle]="{'color':green}"></div>
ngStyle become much more useful when the value is dynamic.
<div [ngStyle]="{'color': person.country==='UK' ? 'green' : 'red'}"></div>


//2
@Component({
  selector: 'app-root',
  template: '
  <p[ngStyle]="{backgroundColor: 'green'}"> Server with ID {{serverID}} is {{serverStatus}}.</p>
  '})
export class AppComponent {
  serverID: number = 10;
  serverStatus: string = 'Offline';

  constructor () {
  this.serverStatus = Math.random() > 0.5 ? 'Online' : 'Offline';
  }
}`.trim();

const customDir = `
//html
<p appCustomdir>Custom directive</p>


//ts
import { Directive, ElementRef } from '@angular/core';

@Directive({
  selector: '[appCustomdir]'
})
export class CustomdirDirective {

  constructor(private el:ElementRef) { 
    el.nativeElement.style.color="green"
  }
}
`.trim();
const templateExpree = `
<h3>{{username}}, welcome to Angular</h3>


//The below javascript expressions are prohibited in template expression
1. assignments (=, +=, -=, ...)
2. new
3. chaining expressions with ; or ,
4. increment and decrement operators (++ and --)
`.trim();

const templateState = `
//template statements appear in quotes to the right of the = symbol like (event)="statement".
<button (click)="statement()">Edit Profile</button>
`.trim();

const twoWay = `
@Component({
  selector: 'app-root',
  template: '
    <input type="text" [(ngModel)]="name" placeholder="type here" />
    <h3>{{ name }}</h3>
  ',
})
export class AppComponent {
  name = 'Angular 5';
}
`.trim();

const hostBinding = `
import { Component, ElementRef, HostBinding, HostListener} from '@angular/core';

@Component({
  selector: 'app-root',
  template: '
      <p>@HostBinding() & @HostListener()</p>
  ',
  providers:[]
})

export class AppComponent{
  constructor(private el:ElementRef){

  }

  @HostBinding('style.color') textColor: any;
  ngOnInit(){
    this.textColor="green";
  }

  @HostListener('click') onClick(){
    // alert('HostListener')
    this.textColor = "red";
  }

  @HostListener('mouseover') onMouseHover(){
    this.textColor = "blue";
  }

  @HostListener('mouseout') onMouseOut(){
    this.textColor = "pink";
  }
}`.trim();

const hostListener = `
mport { Component, HostListener } from '@angular/core';

@Component({
    selector: 'my-component',
    template: '<div>Method decorator</div>'
})
export class MyComponent {
    @HostListener('click', ['$event'])
    onHostClick(event: Event) {
        // clicked, 'event' available
    }
}`.trim();

const parameters = `
//myservice.ts
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class myService {
  name = 'Mukesh';
  constructor() {}
}


//app.component.ts
import { Component, Inject } from '@angular/core';
import { myService } from './myservice';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  constructor(@Inject(myService) myService: any) {
    console.log(myService);
  }
}
`.trim();

const makeServices = `
//myService.ts
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class myService {
  name = 'Mukesh';
  sayHello() {
    console.log(this.name);
  }
}


//app.component.ts
import { myService } from './myservice';

@Component({
  selector: 'app-root',
  template: '',
})
export class AppComponent {
  constructor(private myServices: myService) {
    this.myServices.sayHello();
  }
}


//app.module.ts
@NgModule({
  declarations: [AppComponent, Child],
  imports: [
    BrowserModule,
    AppRoutingModule,
    FormsModule,
    StoreModule.forRoot({}),
  ],
  providers: [myService],
  bootstrap: [AppComponent],
})
`.trim();

const angulconstar = `
export class AppComponent  {
  name = 'Angular';
  selectedUser = null;
  searching: boolean = false;
}
`.trim();

const postals = `
  class Number{
    constructor(){}
  }

  class Address{
    constructor(){}
  }

  class PostalDetails{
    Number;
    Address;

    constructor(){
      this.Number = new Number();
      this.Address = new Address();
    }
  }
`.trim();

