const httpClients = `
import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Kafein {
  name: string;
  address: string;
}

@Component({
  selector: 'app-root',
  template: ' <tr *ngFor="let datas of httpData">
    <td>{{ datas.title }}</td>
  </tr>',
})
export class AppComponent {
  url = 'https://jsonplaceholder.typicode.com/todos';
  httpData: any;

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.callApi();
  }

  callApi() {
    this.http.get<Kafein[]>(this.url).subscribe((data) => {
      this.httpData = data;
    });
  }
}
`.trim();

const stateFun = `
state('open', style({
  height: '300px',
  opacity: 0.5,
  backgroundColor: 'blue'
})),`.trim();

const transition = `
transition('open => closed', [
  animate('500ms')
]),`.trim();

const pipeFor = `
@Component({
  selector: 'app-birthday',
  template: '<p> Birthday is {{birthday | date}}</p>'
})
export class BirthdayComponent {
  birthday = new Date(1987, 6, 18); 
}`.trim();

const paraPipes = `
@Component({
      selector: 'app-birthday',
      template: '<p> Birthday is {{birthday | date: 'dd/MM/yyyy'}}</p>' // 18/06/1987
    })
    export class BirthdayComponent {
      birthday = new Date(1987, 6, 18);
    }`.trim();

const chainPipe = `
@Component({
          selector: 'app-birthday',
          template: '<p> Birthday is {{birthday | date: 'fullDate' | uppercase}} </p>' // THURSDAY, JUNE 18, 1987
        })
        export class BirthdayComponent {
          birthday = new Date(1987, 6, 18);
        }`.trim();

const purpose = `
@Component({
  selector: 'async-observable-pipe',
  template: '<div> <code>observable|async</code>:
  Time: {{time | async}}</div >'
})
export class AsyncObservablePipeComponent {
  time = new Observable(observer =>
    setInterval(() => observer.next(new Date().toString()), 2000)
  );
}`.trim();

const ViewContainerRef =
  `let componentRef = viewContainerRef.createComponent(componentFactory);`.trim();

const template = `
<p>Hip!</p>
<ng-template>
  <p>Hip!</p>
</ng-template>
<p>Hooray!</p>

//<ng-container>
@Component({
  selector: 'app-root',
  template: '
      <ng-template #loading>
        ng-template rendering
      </ng-template>

      <p>ng-container</p>
      <ng-container *ngIf="display else loading">
        ng-container rendering

      </ng-container>
      <ul>
          <li *ngFor="let emp of employees; let i=index">
            <ng-container *ngIf="emp.salery>10000">
              {{i+1}} - {{emp.name}}
            </ng-container>
          </li>
        </ul>
  '',
  providers:[]
})

export class AppComponent {
  display=false;

  employees = [
    {"name":"Mukesh", "salery":10000},
    {"name":"Rakesh", "salery":12000},
    {"name":"Nitesh", "salery":15000},
