}`.trim();

const dslSyntax = `
(): Used for Output and DOM events.
[]: Used for Input and specific DOM element attributes.
*: Structural directives(*ngFor or *ngIf) will affect/change the DOM structure.`.trim();

const multicasting = `
var source = Rx.Observable.from([1, 2, 3]);
var subject = new Rx.Subject();
var multicasted = source.multicast(subject);

// These are, under the hood, 'subject.subscribe({ ...})':
multicasted.subscribe({
  next: (v) => console.log('observerA: ' + v)
});
multicasted.subscribe({
  next: (v) => console.log('observerB: ' + v)
});`.trim();

const changeDetector = `
import { ChangeDetectionStrategy, Component } from '@angular/core';

@Component({
  selector: 'app-root',
  template: '
  <button (click)='updateName()'>Update</button>
  Welcome {{userValue.firstName}} - {{userValue.lastName}}
  <div *ngFor="let str of myStrArr">{{str}}</div>
  <br/>
  <p>onPush Stretgy</p>
  <button (click)='onClick()'>onPush</button>
  ',

  changeDetection:ChangeDetectionStrategy.OnPush
})
export class AppComponent {
  title = '3_change_detection';
  userValue:any;
  myStrArr:any[]=[];

  ngOnInit():void{
    this.userValue = {
      firstName:'Mukesh',
      lastName:'Singh'
    };
  }

  updateName(){
    this.userValue.firstName='Rakesh';
    console.log('default Stretgy')
  }

  onClick(){
    this.myStrArr.push('onPush Stretgy')
    console.log('onPush')
  }
}
`.trim();

