const maxLength = `
function maxLength(arr) {
  let max = 0

  function isUnique(s = ''){
    const map = {}
    for (const c of s) {
      map[c] = (map[c] || 0) + 1
      if (map[c] >= 2) {
        return false
      }
    }
    return true
  }

  function aux (index, current = ''){
    if (index > arr.length - 1) {
      max = Math.max(current.length, max)
      return
    }
    if (isUnique(current)) {
      aux(index + 1, current)
      if (isUnique(current + arr[index])) {
        aux(index + 1, current + arr[index])
      }
    }
  }
  aux(0)
  return max
}

console.log(maxLength(["un","iq","ue"]))
`.trim();


const isIsomorphic = `
function isIsomorphic(s, t = ''){
  const count = str => Array.prototype.reduce.call(str, (map, c, index) => {
    if (map[c]) {
      map[c].push(index)
    }
    map[c] = [index]
    return map
  }, {})

  const mapS = count(s)
  const mapT = count(t)

  for (let i = 0; i < s.length; i++) {
    const charS = s[i]
    const charT = t[i]
    for (let j = 0; j < mapS[charS].length; j++) {
      if (mapS[charS][j] !== mapT[charT][j]) return false;
    }
  }
  return true
}

console.log(isIsomorphic("egg", "add"))
`.trim();

