const removeDuplicates = `
function removeDuplicates(S) {
  const stack = []

  for (const c of S) {
    if (c === stack[stack.length - 1]) {
      while (stack[stack.length - 1] === c) {
        stack.pop()
      }
    } else {
      stack.push(c)
    }
  }
  return stack.join('')
}

