  let newS = ''
  for (const c of s) {
    if (/[a-zA-Z0-9]/.test(c)) {
      newS += c.toLowerCase()
    }
  }

  for (let i = 0; i < Math.floor(newS.length / 2); i++) {
    if (newS[i] !== newS[newS.length - 1 - i]) {
      return false
    }
  }
  return true
}

