z-index: -1 / 1 / 3;

overflow: visible / hidden / scroll / auto;
overflow-y: scroll; 
`.trim();

const supports = `
@supports (display: grid) {
	div {
		display: grid;
	}
}
`.trim();

const counters = `
body {
  counter-reset : h2;
    }

h2 {
  counter-reset : h3;
  }

h3 {
  counter-reset : h4;
  }

h4 {
  counter-reset : h5;
  }

h5 {
  counter-reset : h6;
  }

article h2:before {
  content : counter(h2,decimal) ". ";
  counter-increment : h2;
  }

article h3:before {
  content : counter(h2,decimal) "." counter(h3,decimal) ". ";
  counter-increment : h3;
    }

article h4:before {
  content : counter(h2,decimal) "." counter(h3,decimal) "." counter(h4,decimal) ". ";
  counter-increment : h4;
  }

article h5:before {
  content : counter(h2,decimal) "." counter(h3,decimal) "." counter(h4,decimal) "." counter(h5,decimal) ". ";
  counter-increment : h5;
  }

article h6:before {
  content : counter(h2,decimal) "." counter(h3,decimal) "." counter(h4,decimal) "." counter(h5,decimal)
   "." counter(h6,decimal) ". ";
  counter-increment : h6;
  }

h2.nocount:before, h3.nocount:before, h4.nocount:before, h5.nocount:before, h6.nocount:before {
  content : "";
  counter-increment : none;
    }


//HTML
<article>
<h1>Article H1 title</h1>
  <h2>H2 Title</h2>
  <h2>H2 Title</h2>
    <h3>H3 Title</h3>
      <h4>H4 Title</h4>
        <h5>H5 Title</h5>
          <h6>H6 Title</h6>
</article>
`.trim();

const nthchild = `
div:nth-child(2) {
  background: red;
}

/* Selects the second li element in a list */
li:nth-child(2) {
  background: lightgreen;
}

/* Selects every third element among any group of siblings */
:nth-child(3) {
  background: yellow;
}

//HTML
<div>
  <p>This is some text.</p>
</div>

<div>
  <p>This is some text.</p>
</div>

<div>
  <p>This is some text.</p>
</div>

<ul>
  <li>First list item</li>
  <li>Second list item</li>
  <li>Third list item</li>
  <li>Fourth list item</li>
  <li>Fifth list item</li>
</ul>
`.trim();

const tweening = `
p {
  animation-duration: 2s;
  animation-name: slidethrough;
}

@keyframes slidethrough {
  from {
    margin-left: 100%;
    width: 300%; 
  }

  to {
    margin-left: 0%;
    width: 100%;
  }
}
`.trim();

const defoultcss = `
.resto {
  color: green;
}

#blue {
  color: blue;
}

#initial {
  color: initial/ inherit;
}
`.trim();

const sprites = `
.sprites {
  width: 200px;
  background-image: url("abc.png");
  background-position: 0px -200px;  //second image start after 200px. initials 200px occupied by first image
}
`.trim();

const gridspace = `
//display
Values:
	grid – generates a block-level grid
	inline-grid – generates an inline-level grid

	.container {
      display: grid | inline-grid;
      }


//grid-template-rows:
.container {
  grid-template-columns: [first] 40px [line2] 50px [line3] auto [col4-start] 50px [five] 40px [end];
  grid-template-rows: [row1-start] 25% [row1-end] 100px [third-line] auto [last-line];
}


//Note that a line can have more than one name. For example, here the second line will have 
//two names: row1-end and row2-start:
.container {
  grid-template-rows: [row1-start] 25% [row1-end row2-start] 25% [row2-end];
}


//If your definition contains repeating parts, you can use the repeat() notation to streamline things:
.container {
  grid-template-columns: repeat(3, 20px [col-start]);

 	Which is equivalent to this
  grid-template-columns: 20px [col-start] 20px [col-start] 20px [col-start];
}


//If multiple lines share the same name, they can be referenced by their line name and count
.item {
  grid-column-start: col-start 2;
}.


//grid-template-areas
.item-a {
  grid-area: header;
}
.item-b {
  grid-area: main;
}
.item-c {
  grid-area: sidebar;
}
.item-d {
  grid-area: footer;
}


.container {
  display: grid;
  grid-template-columns: 50px 50px 50px 50px;
  grid-template-rows: auto;
  grid-template-areas:
    "header header header header"
    "main main . sidebar"
    "footer footer footer footer";
}
`.trim();

const gridtemplates = `
//
.container {
  grid-template: none | grid-template-rows / grid-template-columns;
}


//Specifies the size of the grid lines. Setting the width of the gutters between the columns/rows
.container {
	column-gap: 15px 10px;
	row-gap: 15px 10px;
