import React, { Component } from "react";
import Prism from "prismjs";
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import "../ReactJs/styles.css";
import Sidebar from "./sidebar";
import PrismCode from "../ReactJs/prismCode";

const titles = { backgroundColor: "#F0F8FF", padding: "1px", fontSize: "16px" };

const styles = (theme) => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1),
  },
  smMargin: {
    margin: theme.spacing(1),
  },
  actionDiv: {
    textAlign: "center",
  },
});

const accept = `
<input type="file" id="soundFile" accept="audio/*">
<input type="file" id="videoFile" accept="video/*">
<input type="file" id="imageFile" accept="image/*" multiple>
<input type="file" id="profile_pic" name="profile_pic" accept=".jpg, .jpeg, .png">
 `.trim();

const iframe = `
<iframe src="https://example.org"
    title="iframe" width="400" height="300">
</iframe>


//
<address>Delhi, India</address>


//
<citr>The Title Of Books</citr> Book written by ...


//
<marquee direction="right">Moving Text</marquee>
<marquee direction="up">Moving Text</marquee><br/>
 `.trim();

const autocapitalize = `
#syntex
<tag_name autocapitalize="off | none | on | sentences | words | characters" />


#Ex.
div.a { text-transform: uppercase/ lowercase/ capitalize; }
<div class="a">Uppercase.</div>


//
<a href="https://www.google.com" target="_blank">Open new tab</a><br/>
<a href="#local">SPA</a>
<p id="local">Move to SPA Block</p>
`.trim();

const audio = `
<figure>
    <figcaption>Listen to the T-Rex:</figcaption>
    <audio controls>
      <source src="horse.mp3" type="audio/mpeg">
    </audio>
</figure>`.trim();

const video = `
<video width="320" height="240" controls>
  <source src="movie.mp4" type="video/mp4">
  <source src="movie.ogg" type="video/ogg">
</video>
`.trim();

const capture = `
	<input type="file" id="soundFile" capture="user" accept="audio/*">
<input type="file" id="videoFile" capture="environment" accept="video/*">
<input type="file" id="imageFile" capture="user" accept="image/*">
`.trim();

const textarea = `
<textarea id="story" name="story" rows="5" cols="33">
  It was a dark and stormy night...
</textarea>
`.trim();

const area = `
<map name="planetmap">
  <area shape="rect" coords="0,0,82,126" alt="Sun" href="sun.htm">
  <area shape="circle" coords="90,58,3" alt="Mercury" href="mercur.htm">
  <area shape="circle" coords="124,58,8" alt="Venus" href="venus.htm">
</map>
`.trim();

const object = `
<object data="movie.mp4" width="400" height="300"></object>

<object data="snippet.html" width="500" height="200"></object>
`.trim();

const optgroup = `
<form action="/action_page.php">
  <label for="cars">Choose a car:</label>
  <select name="cars" id="cars">
    <optgroup label="Swedish Cars">
      <option value="volvo">Volvo</option>
      <option value="saab">Saab</option>
    </optgroup>
  </select>
  <input type="submit" value="Submit">
</form>
`.trim();

const outputs = `
<form oninput="x.value=parseInt(a.value)+parseInt(b.value)">
  <input type="range" id="a" value="50">
  +<input type="number" id="b" value="25">
  =<output name="x" for="a b"></output>
</form>
`.trim();

const section = `
<section>
  <p>The he World Wildlife Fund. WWF was founded in 1961.</p>
</section>
`.trim();

const geolocation = `
//index.js
function getLocation() {
  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition(showPosition);
  } else {
    x.innerHTML = "Geolocation is not supported by this browser.";
  }
}

function showPosition(position) {
  x.innerHTML = "Latitude: " + position.coords.latitude +
    "<br>Longitude: " + position.coords.longitude;
}

var x = document.getElementById("demo");


//index.html
  <body>
    <button onclick="getLocation()">Get Location</button>
    <p id="demo"></p>
    <script type="text/javascript" src="index.js"></script>
  </body>
`.trim();

const dragDrop = `
//index.js
function allowDrop(ev) {
  ev.preventDefault();
}

function drag(ev) {
  ev.dataTransfer.setData("text", ev.target.id);
}

function drop(ev) {
  ev.preventDefault();
  var data = ev.dataTransfer.getData("text");
  ev.target.appendChild(document.getElementById(data));
}


//index.html
<style>
#div1, #div2 {
  float: left;
  width: 100px;
  height: 35px;
  border: 1px solid black;
}
</style>
  <script type="text/javascript" src="index.js"></script>
</head>

<body>
<div id="div1" ondrop="drop(event)" ondragover="allowDrop(event)">
  <img src="img_w3slogo.gif" draggable="true" ondragstart="drag(event)" id="drag1" width="88" height="31">
</div>

  <div id="div2" ondrop="drop(event)" ondragover="allowDrop(event)"></div>
</body>
`.trim();

const workerWorker = `var worker = new Worker("sample_prog.js"﴿;`.trim();

const serverSentEvent = `
var source = new EventSource("sse_demo.php");
source.onmessage = function(event) {
document.getElementById("result").innerHTML += event.data + "<br>";
};
`.trim();

const element = `
<script>
document.createElement("myElement")
</script>


//It can be used in the HTML as.
<myElement>hello</myElement>
`.trim();

const Session = `
//Use setItem() function to store data in Session Storage
sessionStorage.setItem (‘key’,’value’);

sessionStorage.setItem (‘username’,’Meenakshi’)


//Use getItem() function to retrieve data from Session Storage
sessionStorage.getItem(‘key’);

var username= sessionStorage.getItem(‘username’);


/*
  We can only store String in Session Storage. To save the objects in Session, first, convert 
  the object into JSON string and then store this string in Session Storage as
*/

sessionStorage.setItem (‘object’, JSON.stringify(object));


//If JSON string gets stored in Session Storage, then first convert it into an object as follows.

var object=JSON.parse(sessionStorage.getItem(‘object’));


//Use removeItem() function to delete a particular key from Session Storage.
sessionStorage.removeItem(‘key’);

`.trim();

const pres = `
<pre>
   My Bonnie lies over the ocean.
</pre>`.trim();

const htmlStyles = `
<tagname style="property:value;">`.trim();

const quotation = `
q for: <q>Short Quotations.</q>

<abbr title="World Health Organization">WHO</abbr>

<bdo dir="rtl">This text will be written from right to left</bdo>
`.trim();

const address = `
<p>Email link <a href="mailto: admin@gmail.com">admin@gmail.com</a></p>


//
Read Pdf by embed, iframe and object.
<embed src="CM_Process.pdf" type="application/pdf"><br/>
`.trim();

const htmltags = `
Tags: There are 2 types of HTML Tags.
1. Cntainer Tag <p></p>
2. Empty Tag <hr/>

Element: Element is combination of Tag and containt.
Ex. <p>Here is containt</p>

HTML Attributes: Attributes provide additional information about an element.
Always specifed in the start tag.
Come in name/ value pairs. Like name="value"
Ex. <p title="about paragraph">Here is containt</p>
`.trim();

const metertags = `
Temp: <meter min=0 max=10 value=".5" low="3" high="8" optimum="5"></meter>
Download: <progress max="100" value="80"></progress>
`.trim();

const grisCont = `
<style>
  .grid-container {
    display: grid;
    grid-template-columns: auto auto auto;
  }
  .grid-item {
    background-color: rgba(255, 255, 255, 0.8);
    border: 1px solid rgba(0, 0, 0, 0.8);
    text-align: center;
  }
</style>
<body>
  <div class="grid-container">
    <p class="grid-item">1</p>
    <p class="grid-item">2</p>
    <p class="grid-item">3</p>
  </div>
</body>`.trim();

const tablerow = `
<style>
  #customers {
    width: 100%;
  }

  #customers td,
  #customers th {
    border: 1px solid #ddd;
    padding: 8px;
  }

  #customers tr:nth-child(even) {
    background-color: #f2f2f2;
  }

  #customers th {
    text-align: left;
    background-color: #04aa6d;
    color: white;
  }
</style>
<body>
  <table id="customers">
    <tr>
      <th>Company</th>
      <th>Contact</th>
      <th>Country</th>
    </tr>
    <tr>
      <td>Alfreds Futterkiste</td>
      <td>Maria Anders</td>
      <td>Germany</td>
    </tr>
    <tr>
      <td>Berglunds snabbköp</td>
      <td>Christina Berglund</td>
      <td>Sweden</td>
    </tr>
    <tr>
      <td>Centro comercial Moctezuma</td>
      <td>Francisco Chang</td>
      <td>Mexico</td>
    </tr>
  </table>
</body>
`.trim();

const threeImg = `
<style>
  .heading {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
  }
</style>
<body>
  <div class="heading">
    <p>Entrees</p>
    <p>Drinks</p>
    <p>Desserts</p>
  </div>
</body>`.trim();

const imgtags = `
<style>
  .container {
    position: relative;
    text-align: center;
    color: white;
  }

  .bottom-left {
    position: absolute;
    bottom: 8px;
    left: 16px;
  }

  .top-left {
    position: absolute;
    top: 8px;
    left: 16px;
  }

  .top-right {
    position: absolute;
    top: 8px;
    right: 16px;
  }

  .bottom-right {
    position: absolute;
    bottom: 8px;
    right: 16px;
  }

  .centered {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
  }
</style>
<body>
  <div class="container">
    <img src="../assets/img_snow.jpg" alt="Snow" style="width: 100%" />
    <p class="bottom-left">Bottom Left</p>
    <p class="top-left">Top Left</p>
    <p class="top-right">Top Right</p>
    <p class="bottom-right">Bottom Right</p>
    <p class="centered">Centered</p>
  </div>
</body>`.trim();

const roundCor = `
<style>
  #rcorners1 {
    border-radius: 25px;
    background: #73ad21;
    padding: 20px;
    width: 200px;
    height: 150px;
  }
</style>
<body>
  <p id="rcorners1">Rounded corners!</p>
</body>

`.trim();

class Html1 extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0);
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4>
              <Sidebar />
            </h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
              <h3>1. Grid</h3>
              <div style={titles}>
                <PrismCode
                  code={grisCont}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>2. Table</h3>
              <div style={titles}>
                <PrismCode
                  code={tablerow}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>3. Three image inline</h3>
              <div style={titles}>
                <PrismCode
                  code={threeImg}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>4. Image with text</h3>
              <div style={titles}>
                <PrismCode
                  code={imgtags}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>5. RoundCorner</h3>
              <div style={titles}>
                <PrismCode
                  code={roundCor}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <ol>
                <li>
                  <b>Html: </b>Structure
                </li>
                <li>
                  <b>css: </b>Styles
                </li>
              </ol>
              <br />
              <b>
                Div and Span are used to structure webpage. They are structure
                element.
              </b>
              <ol>
                <li>
                  <b>div: </b>Block Element
                </li>
                <li>
                  <b>span: </b>Inline Element
                </li>
              </ol>
              <br />
              <h3>1. What is a tag in HTML?</h3>
              A tag instructs the Browser about how to format the HTML properly.
              <br />
              <h3>2. What is the difference between HTML elements and tags?</h3>
              The HTML element is everything from the start tag to the end tag.
              HTML elements become HTML tags when enclosed within angular
              brackets.
              <br />
              <div style={titles}>
                <PrismCode
                  code={htmltags}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>3. HTML Attributes</h3>
              HTML attributes provide additional information about HTML
              elements.
              <ol>
                <li>
                  All HTML elements can have <b>attributes.</b>
                </li>
                <li>
                  Attributes are always specified in <b>the start tag.</b>
                </li>
                <li>
                  Attributes usually come in name/value pairs like:{" "}
                  <b>name="value"</b>
                </li>
              </ol>
              <br />
              <h3>4. pre Element</h3>
              <ol>
                <li>The HTML 'pre' element defines preformatted text.</li>
                <li>
                  The text inside a 'pre' element is displayed in a fixed-width
                  font (usually Courier), and it preserves both spaces and line
                  breaks.
                </li>
              </ol>
              <div style={titles}>
                <PrismCode
                  code={pres}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>5. HTML Style Attribute</h3>
              <div style={titles}>
                <PrismCode
                  code={htmlStyles}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <h3>
                6. What is the difference between the ‘id’ attribute and the
                ‘class’ attribute of HTML elements?
              </h3>
              <ol>
                <li>
                  {" "}
                  Multiple elements in HTML can have the same class value.
