import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../../ReactJs/styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../../ReactJs/prismCode';

import Stcksval from '../../../assets/stcks.png';

const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const redesign = {
  height: 200,
  width: 500
}

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})


const factorial = `
function factorial(n){
  if(n === 1) return 1;
  return n * factorial(n-1)
 }

console.log(factorial(4));
 `.trim()

const prime = `
function countPrimes(n){
  const map = []

  for (let i = 2; i < n; i++) {
    map[i] = true
  }

  for (let i = 2; i * i < n; i++) {
    if (map[i]) {
      for (let j = i * i; j < n; j += i) {
        map[j] = false
      }
    }
  }
  return map.filter(num => num === true).length
}

console.log(countPrimes(10))
`.trim()

const magicNumber = `
 function magicNum(){
   const random = Math.random(0,1)
   return random;
 }

 function main(){
   const result = Math.random(0, magicNum);
   console.log(result);
 }

 main();
 `.trim();

const possible = `
function isInt(value) {
  var x;
  if (isNaN(value)) {
    return false;
  }
  x = parseFloat(value);
  return (x | 0) === x;
}

console.log(isInt(0));`.trim();

const common_divisor = `
function numbers(x, y) {
    if ((typeof x !== 'number') || (typeof y !== 'number'))
      return false;
      x = Math.abs(x);
      y = Math.abs(y);
        while(y) {
          var i = y;
          y = x % y;
          x = i;
        }
  return x;
  }

  console.log(numbers(12, 4));
  console.log(numbers(9, 3));`.trim()

const addTwoNumbers = `
function ListNode(val) {
  this.val = val
  this.next = null
}

const addTwoNumbers = (l1, l2) => {
  let h1 = l1
  let h2 = l2
  let digit = 0
  let head = null
  let current = null
  while (h1 || h2) {
    const num1 = h1 ? h1.val : 0
    const num2 = h2 ? h2.val : 0
    let value = num1 + num2 + digit
    if (value >= 10) {
      value -= 10
      digit = 1
    } else {
      digit = 0
    }
    h1 = h1 ? h1.next : null
    h2 = h2 ? h2.next : null
    const node = new ListNode(value)
    if (!head) {
      head = node
      current = node
    } else {
      current.next = node
      current = node
    }
  }
  if (digit === 1) {
    current.next = new ListNode(1)
  }
  return head
}

console.log(addTwoNumbers())`.trim();

const reverse = `
function reverse(x){
  let max = (2 ** 31) - 1;
  let min = - (2 ** 31);

  const isMinus = x < 0;
  const str = String(Math.abs(x)).split('').reverse().join('')
  const result =  isMinus ? Number('-'$'{str}') : Number(str)
  if (result > max || result < min) return 0;

  return result
}

console.log(reverse(123))`.trim();

const isPowerOfTwo = `
function isPowerOfTwo(x){
  if (x <= 0) return false;
  return (x & (x - 1)) === 0
}

console.log(isPowerOfTwo(16))
`.trim();

const getFactors = `
function getFactors(n){
  const result = [];

  function aux(remain, start = 2, current = []){
    if (remain === 1) {
      if (current.length > 1) {
        result.push([...current])
      }
      return
    }

    for (let i = start; i <= remain; i++) {
      if (remain % i === 0) {
        current.push(i)
        aux(remain / i, i, current)
        current.pop()
      }
    }
  }
  aux(n)
  return result
}

console.log(getFactors(345))
`.trim();

const addDigits = `
function addDigits(num){
  function cal(num){
      var s = num.toString();
      var digits = s.split("").map(Number);

      if(digits.length === 1) return digits[0];

      num = digits.reduce((pre, cur) => (pre + cur));
      return cal(num);
  }

  return cal(num);
};

console.log(addDigits(38))
`.trim();

const isUgly = `
function isUgly(num){
  const memo = [];

  function aux(n){
    if (memo[n] !== undefined) return memo[n];
    if (n === 1 || n === 2 || n === 3 || n === 5 || n === 4) return true;
    if (n < 5) return false;

    memo[n] = aux(n / 2) || aux(n / 3) || aux(n / 5);
    return memo[n]
  }
  return aux(num)
}

console.log(isUgly(15))
`.trim();

const getSum = `
function getSum(a, b) {
  let carry = 0;

  while (b !== 0) {
    carry = a & b
    a ^= b
    b = carry << 1
  }
  return a
}

console.log(getSum(5, 1))
`.trim();

const numSquares = `
function numSquares(n){
  const maxLength = Math.ceil(Math.sqrt(n));
  const squareLengths = new Array(maxLength).fill(0).map((v, index) => (index + 1) ** 2);
  const memo = {};

  function aux(index, sum){
    memo[index] = memo[index] || {}
    if (memo[index][sum] !== undefined) {
      return memo[index][sum]
    }

    if (sum === n) return 0;
    if (sum > n || index > squareLengths.length - 1) return Infinity;

    memo[index][sum] = Math.min(
      aux(index, sum + squareLengths[index]) + 1,
      aux(index + 1, sum),
    )
    return memo[index][sum]
  }
  return aux(0, 0)
}

console.log(numSquares(13))
`.trim();

const brokenCalc = `
function brokenCalc(X, Y){
  let result = 0;

  while (Y > X) {
    if (Y % 2 === 0) {
      Y /= 2
    } 
    else {
      Y += 1
    }
    result += 1
  }
  return result + X - Y
}

console.log(brokenCalc(5, 8))
`.trim();

const addStrings = `
function addStrings(num1, num2){
  const reversedNum1 = num1.split('').reverse();
  const reversedNum2 = num2.split('').reverse();

  const { shorter, longer } = reversedNum1.length < reversedNum2.length
    ? { shorter: reversedNum1, longer: reversedNum2 }
    : { shorter: reversedNum2, longer: reversedNum1 }

  let rem = 0;
  let result = longer.reduce((acc, digit1, index) => {
    const total = parseInt(digit1, 10) + (parseInt(shorter[index], 10) || 0) + rem
    if (total >= 10) {
      rem = 1
    } else {
      rem = 0
    }
    return [...acc, total >= 10 ? total - 10 : total]
  }, [])

  result = rem === 1 ? [...result, rem] : result
  return result.reverse().join('')
}

const zero = (count) => {
  let result = '';

  while (count >= 1) {
    result += '0'
    count -= 1
  }
  return result
}
function multiply(num1 = '', num2 = ''){
  let result = 0;
  for (let i = num2.length - 1; i >= 0; i--) {
    let current = '';
    let base = 0;

    for (let j = num1.length - 1; j >= 0; j--) {
      let currentResult = parseInt(num1[j], 10) * parseInt(num2[i], 10) + base;
      if (currentResult >= 10) {
        base = Math.floor(currentResult / 10);
        currentResult -= base * 10;
      } else {
        base = 0;
      }
      current = '$'{currentResult + current}';
    }
    if (base > 0) {
      current = base + current;
    }

    result = addStrings(result || '0', current + zero(num2.length - 1 - i));
  }
  if (result.length > 0 && result[0] === '0') {
    return '0';
  }
  return result
}

console.log(addStrings("2","3"))
`.trim();

const mySqrt = `
function mySqrt(x){
  let r = x
  while (r * r > x) {
    r = Math.floor((r + x / r) / 2)
  }

  return r
}

console.log(mySqrt(4))
`.trim();

const myPow = `
function myPow(x, n){
  if (n === 0) return 1;

  const temp = myPow(x, Math.floor(Math.abs(n / 2)))
  const result = n % 2 === 0 ? temp * temp : x * temp * temp
  return n < 0 ? 1 / result : result
}

console.log(myPow(2.00000, 10))
`.trim();

const letterCombinations = `
function letterCombinations(digits = ''){
  const map = {
    2: ['a', 'b', 'c'],
    3: ['d', 'e', 'f'],
    4: ['g', 'h', 'i'],
    5: ['j', 'k', 'l'],
    6: ['m', 'n', 'o'],
    7: ['p', 'q', 'r', 's'],
    8: ['t', 'u', 'v'],
    9: ['w', 'x', 'y', 'z'],
  }

  if (!digits.length) return [];

  let result = [''];
  let index = 0;

  while (index < digits.length) {
    const next = map[digits[index]];
    const nextResult = [];

    for (let i = 0; i < result.length; i++) {
      for (let j = 0; j < next.length; j++) {
        nextResult.push(result[i] + next[j])
      }
    }
    result = nextResult;
    index++;
  }
  return result;
}

console.log(letterCombinations("23"))
`.trim();


class DSLogic4 extends Component {
  componentDidMount() {
    setTimeout(() => Prism.highlightAll(), 0)
  }
  render() {
    const { classes } = this.props;
    return (
      <Grid container>
        <Grid item xs={2}>
          <Paper className={classes.paper}>
            <h4><Sidebar /></h4>
          </Paper>
        </Grid>
        <Grid item xs={10}>
          <Paper className={classes.paper}>
            <List>
            <h3>1. Factorial</h3>
              <div style={titles}>
                <PrismCode
                  code={factorial}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>2. Prime</h3>
              <div style={titles}>
                <PrismCode
                  code={prime}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <b>3. Given a function magicNumber() that returns a random integer 1 or 0, write a new function that will generate a random number that uses this magicNumber() function.</b>
              <div style={titles}>
                <PrismCode
                  code={magicNumber}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />
              <br />

              <b>4. Discuss possible ways to write a function isInteger(x) that determines if x is an integer.</b>
              <br />
              <div style={titles}>
                <PrismCode
                  code={possible}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>5. Common Divisor</h3>
              <div style={titles}>
                <PrismCode
                  code={common_divisor}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br />

              <h3>6. Sqrt(x).</h3>
              <b>Input: </b>4<br/>
              <b>Output: </b>2<br/><br/>
              <b>Example 2: </b>

              <b>Input: </b>8<br/>
              <b>Output: </b>2<br/>
              <b>Explanation: </b>The square root of 8 is 2.82842..., and since 
                          the decimal part is truncated, 2 is returned.

              <div style={titles}>
                <PrismCode
                  code={mySqrt}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>7. Pow(x, n).</h3>
              <b>Input: </b>x = 2.00000, n = 10<br/>
              <b>Output: </b>1024.00000
              <div style={titles}>
                <PrismCode
                  code={myPow}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>8. Add Two Numbers.</h3>
              You are given two non-empty linked lists representing two non-negative integers. The digits are stored in reverse order and each of their nodes contain a single digit. Add the two numbers and return it as a linked list.
              <br/>
              You may assume the two numbers do not contain any leading zero, except the number 0 itself.
              <br/>
              <b>Example: </b>
              <b>Input: </b>(2  4  3) + (5  6  4)<br/>
              <b>Output: </b>7  0  8<br/>
              <b>Explanation: </b>342 + 465 = 807.
              <div style={titles}>
                <PrismCode
                  code={addTwoNumbers}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>9. Reverse Integer.</h3>
              <div style={titles}>
                <PrismCode
                  code={reverse}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>



              <h3>11. Factor Combinations.</h3>
              <div style={titles}>
                <PrismCode
                  code={getFactors}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>12. Add Digits.</h3>
              Given a non-negative integer num, repeatedly add all its digits until the result has only one digit.
              <br/>
              <br/>
              <b>Input: </b>38<br/>
              <b>Output: </b>2 <br/>
              <b>Explanation: </b>The process is like: 3 + 8 = 11, 1 + 1 = 2. 
                          Since 2 has only one digit, return it.
              <div style={titles}>
                <PrismCode
                  code={addDigits}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>13. Ugly Number.</h3>
              Write a program to check whether a given number is an ugly number.
              <br/>
              Ugly numbers are positive numbers whose prime factors only include 2, 3, 5.
              <div style={titles}>
                <PrismCode
                  code={isUgly}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>14. Sum of Two Integers.</h3>
              Calculate the sum of two integers a and b, but you are not allowed to use the operator + and -.
              <div style={titles}>
                <PrismCode
                  code={getSum}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>15. Perfect Squares.</h3>
              Given a positive integer n, find the least number of perfect square numbers (for example, 1, 4, 9, 16, ...) which sum to n.
              <div style={titles}>
                <PrismCode
                  code={numSquares}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>16. Broken Calculator.</h3>
              On a broken calculator that has a number showing on its display, we can perform two operations:
              <br/>
              <ul>
                <li><b>Double: </b>Multiply the number on the display by 2, or;</li>
                <li><b>Decrement: </b>Subtract 1 from the number on the display.</li>
              </ul>
              Initially, the calculator is displaying the number X.
              <br/>
              Return the minimum number of operations needed to display the number Y.
              <div style={titles}>
                <PrismCode
                  code={brokenCalc}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/>

              <h3>17. Multiply Strings</h3>
              <b>Input: </b>num1 = "2", num2 = "3" <br/>
              <b>Output: </b>"6"
              <div style={titles}>
                <PrismCode
                  code={addStrings}
                  language="js"
                  plugins={["line-numbers"]}
                />
              </div>
              <br/> 

              <h3>18. Letter Combinations of a Phone Number.</h3>
