import React, { Component } from 'react';
import Prism from "prismjs"
import { Grid, Paper, withStyles, List } from "@material-ui/core";

import '../styles.css'
import Sidebar from '../sidebar';
import PrismCode from '../prismCode';
import Pure from '../../../assets/pure.png';


const titles = { backgroundColor: '#F0F8FF', padding: '1px', fontSize: '16px' }

const redesign = {
  height: 350,
  width: 600
}

const styles = theme => ({
  paper: {
    margin: theme.spacing(1),
    padding: theme.spacing(1)
  },
  smMargin: {
    margin: theme.spacing(1)
  },
  actionDiv: {
    textAlign: "center"
  }
})

const childData = `
const App = () => {
  const [users, setUsers] = useState("");

  const handleText = (myData) => {
    setUsers(myData);
  };

  return (
    <div>
      {users}
      <Child refer={handleText} />
    </div>
  );
};

export default App;

const Child = (props) => {
  const data = useState("first");

  const changeText = () => {
    props.refer(data);
  };

  return (
    <div>
      <button onClick={changeText}>Click</button>
    </div>
  );
};`.trim();

const pureComps = `
const quotes = ["quote1", "quote2", "quote3", "quote4", "quote5"];

const NotFound = () => {
  let randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
  return (
    <h1>
      Quote: <strong>{randomQuote}</strong>
    </h1>
  );
};

export default class Controll extends Component {
  state = { username: "foo" };

  componentDidMount() {
    setTimeout(() => this.setState({ username: "bar" }), 2000); // Simulate async ajax call
  }

  render() {
    return (
      <div>
        Welcome: {this.state.username}
        <NotFound />
      </div>
    );
  }
}
`.trim();

const pureComps2 = `
const quotes = ["quote1", "quote2", "quote3", "quote4", "quote5"];

class NotFound extends React.PureComponent {
  render() {
    let randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
    return (
      <h1>
        Quote: <strong>{randomQuote}</strong>
      </h1>
    );
  }
}

export default class Controll extends Component {
  constructor(props) {
    super(props);
    this.state = { username: "foo" };
  }

  componentDidMount() {
    setTimeout(() => this.setState({ username: "bar" }), 2000);
  }

  render() {
    return (
      <div>
        Welcome: {this.state.username}
        <NotFound />
      </div>
    );
  }
}
`.trim();

const PureComponents = `
//App.js
import Task from '../comp';

class App extends PureComponent {
state = {
     taskList: [
        { title: 'excercise'},
        { title: 'cooking'},
        { title: 'Reacting'},
     ]
  };

  componentDidMount() {
  setInterval(() => {
     this.setState({taskList: this.state.taskList});
  }, 1000);
}

render() {
  return (
      <div>
         {this.state.taskList.map((task, i) => {
            return (<Task
               key={i}
               title={task.title}
            />);
         })}
      </div>
    );
  }
}

export default App;


//text.js
class Task extends React.Component {
  render() {
     console.log('task added');
     return (
          <div>
            {this.props.title}
          </div>);
  }
}

export default Task;
`.trim();

